INSERT INTO `npc_text` VALUES (6194, 'Season\'s savings to you and yours!', 'Season\'s savings to you and yours!', 0, 1, 0, 0, 0, 0, 0, 0, 'Fresh from our farm to your plate - it\'s Smokywood Pastures wholesome goodness!', 'Fresh from our farm to your plate - it\'s Smokywood Pastures wholesome goodness!', 0, 3, 0, 0, 0, 0, 0, 0, '\'Tis the season for great bargains!  Fah la la la la!', '\'Tis the season for great bargains!  Fah la la la la!', 0, 1, 0, 0, 0, 0, 0, 0, 'Have a wondrous Feast of Winter Veil!  Start it off right with treats from Smokywood Pastures!', 'Have a wondrous Feast of Winter Veil!  Start it off right with treats from Smokywood Pastures!', 0, 3, 0, 0, 0, 0, 0, 0, 'Be sure to tell Greatfather Winter what secret surprise you\'d like to get this Winter Veil!', 'Be sure to tell Greatfather Winter what secret surprise you\'d like to get this Winter Veil!', 0, 1, 0, 0, 0, 0, 0, 0, 'Don\'t let the jingle of the bells take away from the jingle in your pocket!  Buy Smokywood Pastures treats for your Winter Veil festivities!', 'Don\'t let the jingle of the bells take away from the jingle in your pocket!  Buy Smokywood Pastures treats for your Winter Veil festivities!', 0, 3, 0, 0, 0, 0, 0, 0, 'If I had some chestnuts, you\'d better believe I\'d have them roasting over an open fire.  Mmmm!', 'If I had some chestnuts, you\'d better believe I\'d have them roasting over an open fire.  Mmmm!', 0, 1, 0, 0, 0, 0, 0, 0, 'Don\'t forget ot stock up on wrapping paper this holiday season!  Nothing says Wondrous Winter Veil like wrapped armors and weapons for your friends and family.', 'Don\'t forget ot stock up on wrapping paper this holiday season!  Nothing says Wondrous Winter Veil like wrapped armors and weapons for your friends and family.', 0, 3, 0, 0, 0, 0, 0, 0, 15595);
