INSERT INTO `npc_text` VALUES (6993, 'Your armor looks scary.  Are you scary too?', '', 1, 1, 0, 0, 0, 0, 0, 0, 'If you\'re a real hero of the Horde, then I wanna see you fight a dragon!  Yeah!', '', 1, 1, 0, 0, 0, 0, 0, 0, 'When lightning strikes the ocean why don\'t all the fish die?', '', 1, 1, 0, 0, 0, 0, 0, 0, 'Would a fly without wings be called a walk?', '', 1, 1, 0, 0, 0, 0, 0, 0, 'I have a rock in my nose.', '', 1, 1, 0, 0, 0, 0, 0, 0, 'If a turtle doesn\'t have a shell, is he homeless or naked?', '', 1, 1, 0, 0, 0, 0, 0, 0, 'What does zug zug mean?', '', 1, 1, 0, 0, 0, 0, 0, 0, 'If your glove is too big, does it still fit like a glove?', '', 1, 1, 0, 0, 0, 0, 0, 0, 15595);
