INSERT INTO `npc_text` VALUES (16635, 'You look exhausted. Is something wrong?', 'You look exhausted. Is something wrong?', 0, 1, 0, 0, 0, 0, 0, 0, 'Good to see you.', 'Good to see you.', 0, 1, 0, 0, 0, 0, 0, 0, 'Yes?', 'Yes?', 0, 1, 0, 0, 0, 0, 0, 0, 'Have you recovered the final piece?', 'Have you recovered the final piece?', 0, 1, 0, 0, 0, 0, 0, 0, 'Any luck with Therazane?', 'Any luck with Therazane?', 0, 1, 0, 0, 0, 0, 0, 0, 'You look intent. Is there something I can help with?', 'You look intent. Is there something I can help with?', 0, 1, 0, 0, 0, 0, 0, 0, 'Am I needed?', 'Am I needed?', 0, 1, 0, 0, 0, 0, 0, 0, 'And I thought I might get a bit of rest. Silly me.', 'And I thought I might get a bit of rest. Silly me.', 0, 1, 0, 0, 0, 0, 0, 0, 15595);
