INSERT INTO `creature_text` VALUES ('18744', '0', '0', 'Stop!', '12', '0', '100', '5', '0', '0', 'Aurosalia');
INSERT INTO `creature_text` VALUES ('18744', '0', '2', '', '12', '1', '100', '274', '0', '0', 'Aurosalia');
INSERT INTO `creature_text` VALUES ('18146', '0', '0', '%s inclines his head slightly to the pair of guardians in greeting.', '16', '0', '100', '0', '0', '0', 'Champion Vranesh');
INSERT INTO `creature_text` VALUES ('18146', '1', '0', '%s arches a sculpted brow at the guards, a disapproving scowl on his face.', '16', '0', '100', '0', '0', '0', 'Champion Vranesh');
INSERT INTO `creature_text` VALUES ('18146', '2', '0', '%s glances from guard to guard.', '16', '0', '100', '0', '0', '0', 'Champion Vranesh');
INSERT INTO `creature_text` VALUES ('18146', '3', '0', '%s surveys the small ranger practice ground with a condescending smirk.', '16', '0', '100', '0', '0', '0', 'Champion Vranesh');
INSERT INTO `creature_text` VALUES ('16222', '0', '0', 'Glory to the Sun K-- ah... Silvermoon!', '12', '1', '100', '0', '0', '0', 'Silvermoon City Guardian');
INSERT INTO `creature_text` VALUES ('16222', '1', '0', 'Our lives are yours, Blood Knight.', '12', '1', '100', '0', '0', '0', 'Silvermoon City Guardian');
INSERT INTO `creature_text` VALUES ('34064', '0', '0', 'We did it, lads! We got here before Algalon\'s arrival. Maybe we can rig the systems to interfere with his analysis--', '14', '0', '0', '0', '0', '15824', 'Brann Bronzebeard - SAY_BRANN_ALGALON_INTRO_1');
INSERT INTO `creature_text` VALUES ('34064', '1', '0', 'I\'ll head back to the Archivum and see if I can jam his signal. I might be able to buy us some time while you take care of him.', '12', '0', '0', '0', '0', '15825', 'Brann Bronzebeard - SAY_BRANN_ALGALON_INTRO_2');
INSERT INTO `creature_text` VALUES ('34064', '2', '0', 'I know just the place. Will you be all right?', '14', '0', '0', '6', '0', '15823', 'Brann Bronzebeard - SAY_BRANN_ALGALON_OUTRO');
INSERT INTO `creature_text` VALUES ('32871', '0', '0', 'Translocation complete. Commencing planetary analysis of Azeroth.', '12', '0', '0', '0', '0', '15405', 'Algalon the Observer - SAY_ALGALON_INTRO_1');
INSERT INTO `creature_text` VALUES ('32871', '1', '0', 'Stand back, mortals. I\'m not here to fight you.', '12', '0', '0', '0', '0', '15406', 'Algalon the Observer - SAY_ALGALON_INTRO_2');
INSERT INTO `creature_text` VALUES ('32871', '2', '0', 'It is in the universe\'s best interest to re-originate this planet should my analysis find systemic corruption. Do not interfere.', '12', '0', '0', '0', '0', '15407', 'Algalon the Observer - SAY_ALGALON_INTRO_3');
INSERT INTO `creature_text` VALUES ('32871', '3', '0', 'Your actions are illogical. All possible results for this encounter have been calculated. The Pantheon will receive the Observer\'s message regardless of outcome.', '14', '0', '0', '0', '0', '15386', 'Algalon the Observer - SAY_ALGALON_START_TIMER');
INSERT INTO `creature_text` VALUES ('32871', '4', '0', 'See your world through my eyes: A universe so vast as to be immeasurable - incomprehensible even to your greatest minds.', '14', '0', '0', '1', '0', '15390', 'Algalon the Observer - SAY_ALGALON_AGGRO');
INSERT INTO `creature_text` VALUES ('32871', '5', '0', 'The stars come to my aid!', '14', '0', '0', '0', '0', '15392', 'Algalon the Observer - SAY_ALGALON_COLLAPSING_STAR');
INSERT INTO `creature_text` VALUES ('32871', '6', '0', '%s begins to Summon Collapsing Stars!', '41', '0', '0', '0', '0', '0', 'Algalon the Observer - EMOTE_ALGALON_COLLAPSING_STAR');
INSERT INTO `creature_text` VALUES ('32871', '7', '0', 'Witness the fury of the cosmos!', '14', '0', '0', '0', '0', '15396', 'Algalon the Observer - SAY_ALGALONG_BIG_BANG');
INSERT INTO `creature_text` VALUES ('32871', '8', '0', '%s begins to cast Big Bang!', '41', '0', '0', '0', '0', '0', 'Algalon the Observer - EMOTE_ALGALON_BIG_BANG');
INSERT INTO `creature_text` VALUES ('32871', '9', '0', 'You are out of time.', '14', '0', '0', '0', '0', '15394', 'Algalon the Observer - SAY_ALGALON_ASCEND');
INSERT INTO `creature_text` VALUES ('32871', '10', '0', '%s begins to cast Cosmic Smash!', '41', '0', '0', '0', '0', '0', 'Algalon the Observer - EMOTE_ALGALON_COSMIC_SMASH');
INSERT INTO `creature_text` VALUES ('32871', '11', '0', 'Behold the tools of creation!', '14', '0', '0', '0', '0', '15397', 'Algalon the Observer - SAY_ALGALON_PHASE_TWO');
INSERT INTO `creature_text` VALUES ('32871', '12', '0', 'I have seen worlds bathed in the Makers\' flames, their denizens fading without as much as a whimper. Entire planetary systems born and razed in the time that it takes your mortal hearts to beat once. Yet all throughout, my own heart devoid of emotion... of empathy. I. Have. Felt. Nothing. A million-million lives wasted. Had they all held within them your tenacity? Had they all loved life as you do?', '14', '0', '0', '1', '0', '15393', 'Algalon the Observer - SAY_ALGALON_OUTRO_1');
INSERT INTO `creature_text` VALUES ('32871', '13', '0', 'Perhaps it is your imperfections... that which grants you free will... that allows you to persevere against all cosmically calculated odds. You prevail where the Titan\'s own perfect creations have failed.', '14', '0', '0', '1', '0', '15401', 'Algalon the Observer - SAY_ALGALON_OUTRO_2');
INSERT INTO `creature_text` VALUES ('32871', '14', '0', 'I\'ve rearranged the reply code - your planet will be spared. I cannot be certain of my own calculations anymore.', '14', '0', '0', '1', '0', '15402', 'Algalon the Observer - SAY_ALGALON_OUTRO_3');
INSERT INTO `creature_text` VALUES ('32871', '15', '0', 'I lack the strength to transmit the signal. You must... hurry... find a place of power... close to the skies.', '14', '0', '0', '1', '0', '15403', 'Algalon the Observer - SAY_ALGALON_OUTRO_4');
INSERT INTO `creature_text` VALUES ('32871', '16', '0', 'Do not worry about my fate, Bronzen. If the signal is not transmitted in time, re-origination will proceed regardless. Save... your world...', '14', '0', '0', '1', '0', '15404', 'Algalon the Observer - SAY_ALGALON_OUTRO_5');
INSERT INTO `creature_text` VALUES ('32871', '17', '0', 'Analysis complete. There is partial corruption in the planet\'s life-support systems as well as complete corruption in most of the planet\'s defense mechanisms.', '12', '0', '0', '0', '0', '15398', 'Algalon the Observer - SAY_ALGALON_DESPAWN_1');
INSERT INTO `creature_text` VALUES ('32871', '18', '0', 'Begin uplink: Reply Code: \'Omega.\' Planetary re-origination requested.', '12', '0', '0', '0', '0', '15399', 'Algalon the Observer - SAY_ALGALON_DESPAWN_2');
INSERT INTO `creature_text` VALUES ('32871', '19', '0', 'Farewell, mortals. Your bravery is admirable, for such flawed creatures.', '12', '0', '0', '0', '0', '15400', 'Algalon the Observer - SAY_ALGALON_DESPAWN_3');
INSERT INTO `creature_text` VALUES ('32871', '20', '0', 'Loss of life unavoidable.', '14', '0', '0', '0', '0', '15387', 'Algalon the Observer - SAY_ALGALON_KILL');
INSERT INTO `creature_text` VALUES ('32871', '20', '1', 'I do what I must.', '14', '0', '0', '0', '0', '15388', 'Algalon the Observer - SAY_ALGALON_KILL');
INSERT INTO `creature_text` VALUES ('16577', '0', '0', 'Brothers and sisters, I have been to the promised land. I have tasted in the sublime energy. I have felt bliss - bliss so engrossing and all encompassing that I was left wondering if I had stumbled upon the dreams of gods.', '12', '1', '100', '5', '0', '0', 'Martik Tor\'seldoi');
INSERT INTO `creature_text` VALUES ('16577', '1', '0', 'In paradise, you merely reach out and take from the Nether.', '12', '1', '100', '1', '0', '0', 'Martik Tor\'seldoi');
INSERT INTO `creature_text` VALUES ('16577', '2', '0', 'From the very air! Tendrils of arcane energy light up the obsidian sky as plumes of raw magic rise up from fissures in the land. Kneel and drink from the fissure as you do from a stream or well. Pluck a tendril from the heavens as if it were an apple hanging from a branch.', '12', '1', '100', '5', '0', '0', 'Martik Tor\'seldoi');
INSERT INTO `creature_text` VALUES ('16578', '0', '0', 'Tell us more, Martik. What is it? What does it feel like?', '12', '1', '100', '1', '0', '0', 'Blood Elf Pilgrim');
INSERT INTO `creature_text` VALUES ('16578', '1', '0', 'But how?', '12', '1', '100', '6', '0', '0', 'Blood Elf Pilgrim');
INSERT INTO `creature_text` VALUES ('16578', '2', '0', '%s gasps.', '16', '1', '100', '0', '0', '0', 'Blood Elf Pilgrim');
INSERT INTO `creature_text` VALUES ('17226', '0', '0', 'Ok, let\'s go.  I know just the perfect spot!', '12', '0', '100', '0', '0', '0', 'Viera Sunwhisper');
INSERT INTO `creature_text` VALUES ('17226', '1', '0', 'I can\'t wait to try this wine!', '12', '0', '100', '0', '0', '0', 'Viera Sunwhisper');
INSERT INTO `creature_text` VALUES ('17226', '2', '0', 'What... is happening... to me?  Get this cat away from me!', '12', '0', '100', '0', '0', '0', 'Viera Sunwhisper');
INSERT INTO `creature_text` VALUES ('17226', '3', '0', 'Viera Sunwhisper scurries away, attempting to flee from Twinkle.', '16', '0', '100', '0', '0', '0', 'Viera Sunwhisper');
INSERT INTO `creature_text` VALUES ('17230', '0', '0', 'Twinkle looks at Viera Sunwhisper with very hungry eyes.', '16', '0', '100', '0', '0', '0', 'Twinkle');
INSERT INTO `creature_text` VALUES ('26902', '0', '0', 'How dare you summon me without an offering!', '42', '0', '0', '0', '0', '0', 'Essence of Warlord Jin\'arrak');
INSERT INTO `creature_text` VALUES ('33134', '5', '1', 'Powerless to act....', '14', '0', '100', '0', '0', '15778', 'Sara - SAY_SARA_KILL');
INSERT INTO `creature_text` VALUES ('33134', '5', '0', 'Could they have been saved?', '14', '0', '100', '0', '0', '15779', 'Sara - SAY_SARA_KILL');
INSERT INTO `creature_text` VALUES ('33134', '4', '0', 'Let hatred and rage guide your blows!', '14', '0', '100', '0', '0', '15774', 'SAY_SARA_BLESSING_HIT');
INSERT INTO `creature_text` VALUES ('33134', '3', '0', 'Yes! YES! Show them no mercy! Give no pause to your attacks!', '14', '0', '100', '0', '0', '15773', 'Sara - SAY_SARA_FERVOR_HIT');
INSERT INTO `creature_text` VALUES ('33134', '2', '0', 'The time to strike at the head of the beast will soon be upon us! Focus your anger and hatred on his minions!', '14', '0', '100', '457', '0', '15775', 'Sara - SAY_SARA_AGGRO');
INSERT INTO `creature_text` VALUES ('33134', '1', '0', 'What do you want from me? Leave me alone!', '14', '0', '100', '0', '0', '15772', 'Sara - SAY_SARA_ULDUAR_SCREAM_2');
INSERT INTO `creature_text` VALUES ('33134', '0', '0', 'HELP ME! Please get them off of me!', '14', '0', '100', '0', '0', '15771', 'Sara - SAY_SARA_ULDUAR_SCREAM_1');
INSERT INTO `creature_text` VALUES ('9816', '0', '0', '%s begins to regain its strength!', '16', '0', '100', '0', '0', '0', 'Pyroguard Emberseer');
INSERT INTO `creature_text` VALUES ('9816', '1', '0', '%s is nearly at full strength!', '16', '0', '100', '0', '0', '0', 'Pyroguard Emberseer');
INSERT INTO `creature_text` VALUES ('9816', '2', '0', '%s regains its power and breaks free of its bonds!', '16', '0', '100', '0', '0', '0', 'Pyroguard Emberseer');
INSERT INTO `creature_text` VALUES ('9816', '3', '0', 'Ha! Ha! Ha! Thank you for freeing me, fools. Now let me repay you by charring the flesh from your bones.', '14', '0', '100', '0', '0', '0', 'Pyroguard Emberseer');
INSERT INTO `creature_text` VALUES ('4983', '0', '0', 'I noticed some fire on that island over there. A human, too. Let\'s go check it out, $n.', '12', '1', '0', '0', '0', '0', 'Ogron');
INSERT INTO `creature_text` VALUES ('4983', '1', '0', 'That\'s Reethe alright. Let\'s go see what he has to say, yeah?', '12', '1', '0', '0', '0', '0', 'Ogron');
INSERT INTO `creature_text` VALUES ('4980', '0', '0', 'W-what do you want? Just leave me alone...', '12', '0', '0', '0', '0', '0', 'Paval');
INSERT INTO `creature_text` VALUES ('4980', '1', '0', 'I swear, I didn\'t steal anything from you! Here, take some of my supplies, just go away!', '12', '0', '0', '0', '0', '0', 'Paval');
INSERT INTO `creature_text` VALUES ('4983', '2', '0', 'Just tell us what you know about the Shady Rest Inn, and I won\'t bash your skull in.', '12', '0', '0', '0', '0', '0', 'Ogron');
INSERT INTO `creature_text` VALUES ('4980', '2', '0', 'I... Well, I may of taken a little thing or two from the inn... but what would an ogre care about that?', '12', '0', '0', '0', '0', '0', 'Paval');
INSERT INTO `creature_text` VALUES ('4983', '3', '0', 'Look here, if you don\'t tell me about the fire--', '12', '0', '0', '0', '0', '0', 'Ogron');
INSERT INTO `creature_text` VALUES ('4980', '3', '0', 'Not one step closer, ogre!', '12', '0', '0', '0', '0', '0', 'Paval');
INSERT INTO `creature_text` VALUES ('4980', '4', '0', 'And I don\'t know anything about this fire of yours...', '12', '0', '0', '0', '0', '0', 'Paval');
INSERT INTO `creature_text` VALUES ('4980', '5', '0', 'What was that? Did you hear something?', '12', '0', '0', '0', '0', '0', 'Paval');
INSERT INTO `creature_text` VALUES ('5046', '0', '0', 'Paval Reethe! Found you at last. And consorting with ogres now? No fear, even deserters and traitors are afforded some mercy.', '12', '0', '0', '0', '0', '0', 'Caldwell');
INSERT INTO `creature_text` VALUES ('5046', '1', '0', 'Private, show Lieutenant Reethe some mercy.', '12', '0', '0', '0', '0', '0', 'Caldwell');
INSERT INTO `creature_text` VALUES ('5045', '0', '0', 'Gladly, sir.', '12', '0', '0', '0', '0', '0', 'Hallan');
INSERT INTO `creature_text` VALUES ('4980', '6', '0', '%s staggers backwards as the arrow lodges itself deeply in his chest.', '16', '0', '0', '0', '0', '0', 'Paval');
INSERT INTO `creature_text` VALUES ('4980', '7', '0', 'Ugh... Hallan, didn\'t think you had it in you...', '12', '0', '0', '16', '0', '0', 'Paval');
INSERT INTO `creature_text` VALUES ('5046', '2', '0', 'Now, let\'s clean up the rest of the trash, men!', '12', '0', '0', '0', '0', '0', 'Caldwell');
INSERT INTO `creature_text` VALUES ('4983', '4', '0', 'Damn it! You\'d better not die on me, human!', '12', '0', '0', '0', '0', '0', 'Ogron');
INSERT INTO `creature_text` VALUES ('4983', '5', '0', 'Still with us, Reethe?', '12', '0', '0', '0', '0', '0', 'Ogron');
INSERT INTO `creature_text` VALUES ('4980', '8', '0', 'Must be your lucky day. Alright, I\'ll talk. Just leave me alone. Look, you\'re not going to believe me, but it wa... oh, Light, looks like the girl could shoot...', '12', '0', '0', '0', '0', '0', 'Paval');
INSERT INTO `creature_text` VALUES ('4983', '6', '0', 'Looks dead to me. Not much use to us like this. You\'d better go back and tell Krog what happened.', '12', '0', '0', '0', '0', '0', 'Ogron');
INSERT INTO `creature_text` VALUES ('4983', '7', '0', 'By the way, thanks for watching my back.', '12', '0', '0', '0', '0', '0', 'Ogron');
INSERT INTO `creature_text` VALUES ('4983', '8', '0', 'Me smash! You die!', '12', '0', '0', '0', '0', '0', 'Ogron');
INSERT INTO `creature_text` VALUES ('4983', '8', '1', 'I\'ll crush you!', '12', '0', '0', '0', '0', '0', 'Ogron');
INSERT INTO `creature_text` VALUES ('28328', '0', '0', 'The only thing hurt is your pride, lad. Buck up!', '12', '0', '100', '11', '0', '0', 'Drostan to Lucky Wilhelm');
INSERT INTO `creature_text` VALUES ('28328', '0', '1', 'Stop whining. You\'ve still got your luck.', '12', '0', '100', '11', '0', '0', 'Drostan to Lucky Wilhelm');
INSERT INTO `creature_text` VALUES ('28328', '0', '2', 'Bah, it\'s an improvement.', '12', '0', '100', '0', '0', '0', 'Drostan to Lucky Wilhelm');
INSERT INTO `creature_text` VALUES ('28328', '0', '3', 'Calm down, lad, it\'s just birdshot!', '12', '0', '100', '0', '0', '0', 'Drostan to Lucky Wilhelm');
INSERT INTO `creature_text` VALUES ('28054', '0', '0', 'Not the \'stache! Now I\'m asymmetrical!', '12', '0', '100', '5', '0', '0', 'Lucky Wilhelm');
INSERT INTO `creature_text` VALUES ('28054', '0', '1', 'Ouch! That\'s it, I quit the target business!', '12', '0', '100', '0', '0', '0', 'Lucky Wilhelm');
INSERT INTO `creature_text` VALUES ('28054', '0', '2', 'My ear! You grazed my ear!', '12', '0', '100', '0', '0', '0', 'Lucky Wilhelm');
INSERT INTO `creature_text` VALUES ('28054', '1', '0', 'Good shot!', '12', '0', '100', '4', '0', '0', 'Lucky Wilhelm');
INSERT INTO `creature_text` VALUES ('26421', '1', '0', 'Breaking off a piece of its bark, the %s hands it to you before departing.', '16', '0', '100', '0', '0', '0', 'emote text');
INSERT INTO `creature_text` VALUES ('26421', '0', '0', 'The %s is angered by your request and attacks!', '16', '0', '100', '0', '0', '0', 'emote text');
INSERT INTO `creature_text` VALUES ('26321', '0', '0', 'The %s gives you its thanks.', '16', '0', '100', '0', '0', '1525', 'Lothalor Ancient');
INSERT INTO `creature_text` VALUES ('17893', '0', '0', 'Hey!  Over here!  Yeah, over here... I\'m in this cage!!!', '14', '0', '100', '0', '0', '0', 'Naturalist Bite');
INSERT INTO `creature_text` VALUES ('17957', '0', '0', 'Intrudersss with the prisssoner!  Kill them!!!', '14', '0', '100', '0', '0', '0', 'Coilfang Champion');
INSERT INTO `creature_text` VALUES ('6846', '0', '0', 'The Brotherhood will not be hampered by insects.', '12', '0', '100', '0', '0', '0', 'Defias Dockmaster');
INSERT INTO `creature_text` VALUES ('448', '0', '0', 'More bones to gnaw on...', '12', '0', '100', '0', '0', '0', 'Hogger Agro Say 1');
INSERT INTO `creature_text` VALUES ('448', '0', '1', 'Grrrr... fresh meat!', '12', '0', '100', '0', '0', '0', 'Hogger Agro Say 2');
INSERT INTO `creature_text` VALUES ('23564', '0', '0', '%s loses all self control and begins to attack friend and foe alike!', '16', '0', '100', '0', '0', '0', 'Plagued Dragonflayer Tribesman');
INSERT INTO `creature_text` VALUES ('24199', '0', '0', '%s loses all self control and begins to attack friend and foe alike!', '16', '0', '100', '0', '0', '0', 'Plagued Dragonflayer Handler');
INSERT INTO `creature_text` VALUES ('34723', '0', '2', 'Ware da ship! A harpy! ...wait, dat be another bird.', '12', '1', '100', '25', '0', '0', 'Watcher Tolwe - Random');
INSERT INTO `creature_text` VALUES ('34723', '0', '3', 'Dere be a bird!', '12', '1', '100', '25', '0', '0', 'Watcher Tolwe - Random');
INSERT INTO `creature_text` VALUES ('34715', '0', '0', 'And there\'s Orgrimmar, huge and menacing like always. Take a quick break, boys, then it\'s back to the Bluff!', '12', '1', '100', '1', '0', '0', 'Sky-Captain Blastnut - Dock at Orgrimmar');
INSERT INTO `creature_text` VALUES ('34715', '1', '0', 'Next stop, Thunder Bluff!', '12', '1', '100', '5', '0', '0', 'Sky-Captain Blastnut - Leave for Thunder Bluff');
INSERT INTO `creature_text` VALUES ('34715', '2', '0', 'Watch your step! Don\'t want you becoming a smudge down there. Welcome to Thunder Bluff!', '12', '1', '100', '1', '0', '0', 'Sky-Captain Blastnut - Dock at Thunder Bluff');
INSERT INTO `creature_text` VALUES ('34715', '3', '0', 'Okay, boys, let\'s get this baby over to Orgrimmar!', '12', '1', '100', '25', '0', '0', 'Sky-Captain Blastnut - Leave for Orgrimmar');
INSERT INTO `creature_text` VALUES ('34721', '0', '0', 'Move it, Zippik! We don\'t have all day!', '12', '1', '100', '5', '0', '0', 'Chief Officer Ograh - Leave for Thunder Bluff');
INSERT INTO `creature_text` VALUES ('34721', '1', '0', 'Careful. CAREFUL! Did I feel a shudder? You\'re going to knock that tower down, oaf!', '12', '1', '100', '5', '0', '0', 'Chief Officer Ograh - Dock at Orgrimmar & Thunder Bluff');
INSERT INTO `creature_text` VALUES ('34721', '2', '0', 'We\'re behind schedule! Turn this tub east! EAST!', '12', '1', '100', '5', '0', '0', 'Chief Officer Ograh - Leave for Orgrimmar');
INSERT INTO `creature_text` VALUES ('34766', '0', '0', 'Step right up! The zeppelin to Orgrimmar has arrived! All aboard to Durotar!', '14', '0', '100', '22', '0', '11804', 'Krendle Bigpockets - Dock at Thunder Bluff');
INSERT INTO `creature_text` VALUES ('19216', '0', '0', 'Friends, it is my great pleasure to welcome you to the temple today.', '12', '0', '100', '1', '0', '0', 'Grand Anchorite Almonen');
INSERT INTO `creature_text` VALUES ('19216', '1', '0', 'A\'dal has been most generous in his revelations to me, a humble servant of the Light.', '12', '0', '100', '0', '0', '0', 'Grand Anchorite Almonen');
INSERT INTO `creature_text` VALUES ('19216', '2', '0', 'In accordance with my divine calling, I must share these revealed truths to all who would hear.', '12', '0', '100', '0', '0', '0', 'Grand Anchorite Almonen');
INSERT INTO `creature_text` VALUES ('19216', '3', '0', 'It has been made known to me that inside each of us, the Light resides...', '12', '0', '100', '0', '0', '0', 'Grand Anchorite Almonen');
INSERT INTO `creature_text` VALUES ('19216', '4', '0', '...that it is a gift, given freely to all naturally born beings.', '12', '0', '100', '0', '0', '0', 'Grand Anchorite Almonen');
INSERT INTO `creature_text` VALUES ('19216', '5', '0', 'It manifests itself as a feeling, small at first and easily ignored, that confirms truths and subtly prods one to do good.', '12', '0', '100', '0', '0', '0', 'Grand Anchorite Almonen');
INSERT INTO `creature_text` VALUES ('19216', '6', '0', 'Simple kindnesses, charitable deeds, service to those in need. These are all fruits of the Light.', '12', '0', '100', '0', '0', '0', 'Grand Anchorite Almonen');
INSERT INTO `creature_text` VALUES ('19216', '7', '0', 'It rewards those who heed its promptings with blessings, both seen and unseen.', '12', '0', '100', '1', '0', '0', 'Grand Anchorite Almonen');
INSERT INTO `creature_text` VALUES ('19216', '8', '0', 'Personal reservoirs of hope and faith are strengthened, and one\'s capacity for greater light increases.', '12', '0', '100', '0', '0', '0', 'Grand Anchorite Almonen');
INSERT INTO `creature_text` VALUES ('19216', '9', '0', 'Over the course of time, through obedience to the Light\'s guidance, one becomes more sensitive to its voice, and its power.', '12', '0', '100', '273', '0', '0', 'Grand Anchorite Almonen');
INSERT INTO `creature_text` VALUES ('19216', '10', '0', 'Great is the healing and blessing power of the Light\'s most diligent followers.', '12', '0', '100', '0', '0', '0', 'Grand Anchorite Almonen');
INSERT INTO `creature_text` VALUES ('19216', '11', '0', 'Sadly, there are those who wander through mortality in defiance of the Light.', '12', '0', '100', '1', '0', '0', 'Grand Anchorite Almonen');
INSERT INTO `creature_text` VALUES ('19216', '12', '0', 'From these the Light withdraws, until only darkness remains.', '12', '0', '100', '0', '0', '0', 'Grand Anchorite Almonen');
INSERT INTO `creature_text` VALUES ('19216', '13', '0', 'Where there is no light, despair, loathing and rage thrive.', '12', '0', '100', '5', '0', '0', 'Grand Anchorite Almonen');
INSERT INTO `creature_text` VALUES ('19216', '14', '0', 'We are born into a perilous age, where the forces of darkness are determined to bring about our destruction.', '12', '0', '100', '1', '0', '0', 'Grand Anchorite Almonen');
INSERT INTO `creature_text` VALUES ('19216', '15', '0', 'These are the times to find yourselves in unison with the Light.', '12', '0', '100', '5', '0', '0', 'Grand Anchorite Almonen');
INSERT INTO `creature_text` VALUES ('19216', '16', '0', 'Hear my words, and let the Light inside of you confirm the truth of them.', '12', '0', '100', '0', '0', '0', 'Grand Anchorite Almonen');
INSERT INTO `creature_text` VALUES ('19216', '17', '0', 'Embrace the light that is in you, my friends! Deny yourself all darkness...', '12', '0', '100', '5', '0', '0', 'Grand Anchorite Almonen');
INSERT INTO `creature_text` VALUES ('19216', '18', '0', 'We must endure these trying times faithfully, and one day we will find ourselves victorious.', '12', '0', '100', '5', '0', '0', 'Grand Anchorite Almonen');
