INSERT INTO `creature_text` VALUES ('16550', '0', '0', 'By the seven Ata\'mal crystals!  Others have survived the crash!  Zhanaa, is that you?  It is so good to see you again!', '12', '7', '100', '0', '0', '0', 'Image of Technician Dyvuun');
