INSERT INTO `creature_text` VALUES ('23602', '0', '0', 'It\'s good to see you defending Theramore with pride, friend.', '12', '7', '100', '25', '0', '0', 'Deserter Agitator');
INSERT INTO `creature_text` VALUES ('23602', '0', '1', 'When you\'re off duty, let\'s go to the inn. I\'ll buy a round and we can reminisce about the good old days.', '12', '7', '100', '1', '0', '0', 'Deserter Agitator');
