INSERT INTO `creature_text` VALUES ('23602', '1', '2', 'If you think you can stop us, you\'re mistaken, \'friend.\'', '12', '7', '100', '5', '0', '0', 'Deserter Agitator');
INSERT INTO `creature_text` VALUES ('23602', '1', '3', 'It\'s people like you who weaken the Alliance and invite the Horde to take away all we\'ve fought for!', '12', '7', '100', '5', '0', '0', 'Deserter Agitator');
