INSERT INTO `creature_text` VALUES ('31222', '0', '0', 'When I am done here, I am going to mount your heads upon the walls of Azjol-Nerub!', '14', '0', '0', '0', '0', '0', 'Dark Master say 1');
INSERT INTO `creature_text` VALUES ('31242', '0', '0', 'Ah, there you are! Remember me? Of course you do! Wait right there, I\'m coming down.', '14', '0', '0', '0', '0', '0', 'Sigrid Iceborn say 1');
INSERT INTO `creature_text` VALUES ('31242', '1', '0', 'I told you I\'d be better prepared when next we met. I\'ve fought and won the Hyldsmeet, trained at Valkyrion, and here I am.  Come and get some!', '14', '0', '0', '0', '0', '0', 'Sigrid Iceborn say 2');
INSERT INTO `creature_text` VALUES ('31277', '0', '0', 'ENOUGH!  You tiny insects are not worthy to do battle within this sacred place!  You do not fight with honor!  You do not even ascend! ', '14', '0', '0', '0', '0', '0', 'Thane say 1');
INSERT INTO `creature_text` VALUES ('31277', '1', '0', 'Fight me and die you cowards!', '14', '0', '0', '0', '0', '0', 'Thane say 2');
