INSERT INTO `creature_text` VALUES ('14688', '0', '0', 'Hardly a fitting introduction, Spear-Wife.  Now, who is this outsider that I\'ve been hearing so much about?', '14', '0', '0', '0', '0', '0', 'Prince Sandoval say 1');
INSERT INTO `creature_text` VALUES ('14688', '1', '0', 'I will make this as easy as possible for you.  Simply come here and die.  That is all that I ask for your many trespasses.  For your sullying of this exalted place of battle.', '14', '0', '0', '0', '0', '0', 'Prince Sandoval say 2');
INSERT INTO `creature_text` VALUES ('14688', '2', '0', 'FOR YOUR EFFRONTERY TO THE LICH KING!', '14', '0', '0', '0', '0', '0', 'Prince Sandoval say 3');
INSERT INTO `creature_text` VALUES ('31135', '0', '0', 'Valhalas is yours to win or die in, $n. But whatever you do, stay within the bounds of the arena. To flee is to lose and be dishonored.', '12', '0', '0', '0', '0', '0', 'Greiff quest 1 say 1');
INSERT INTO `creature_text` VALUES ('31135', '1', '1', '$n and $g his : her; comrades in arms have chosen to accept honorable combat within the sacred confines of Valhalas.', '14', '0', '0', '0', '0', '0', 'Greiff quest 1 say 2');
