INSERT INTO `creature_text` VALUES ('24877', '0', '0', 'Look to the seas, as your doom comes with the swell of tides.\n', '12', '0', '100', '0', '0', '0', 'Isuldof Iceheart');
INSERT INTO `creature_text` VALUES ('24874', '0', '0', 'Your offering has come too late, little one. Can you feel the mist closing in upon you? The Kvaldir return...', '12', '0', '100', '0', '0', '0', 'Fengir the Disgraced');
