INSERT INTO `creature_text` VALUES ('18407', '0', '0', 'Damn those ogres. Damn them to the Nether. Won\'t someone save us from these savages! This is the fifth kidnapping in as many days.', '12', '1', '100', '0', '0', '0', 'Warden Bullrok');
