INSERT INTO `creature_text` VALUES ('30331', '2', '0', 'The deeds of your $r servant defy $g his:her; stature, Stormlord. $g His:Her; efforts have succeeded in softening the hearts of my people.', '12', '0', '100', '396', '0', '0', 'King Jokkum - Conversation 20 sec in');
