INSERT INTO `creature_text` VALUES ('23224', '0', '5', 'The outlook is positive, but I\'m still negative.', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '6', 'It pains me to say this, but \"Yes\".', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '7', 'When dwarves fly! They do? Then yes!', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '8', 'Sure, but you\'re not going to like it.', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '9', 'Be quiet \'bout what you hear and see around here, $r.', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '10', 'Unfortunately... yes.', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '11', 'I can\'t see why not, although, I can\'t see a lot of things right now.', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '12', 'I would bet your soul on it.', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '13', 'Yes, but if anyone asks... It wasn\'t me who told you.', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '14', 'Didn\'t you already ask that once? Yes already!', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '15', 'Please... Is Kil\'jaeden red?', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '16', 'Yeah, sure. You just keep thinking that.', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '17', 'I suppose.', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '18', 'Definitely.', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '19', 'Jump three times and dance for ten minutes and it will definitely happen!', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '20', 'Word on the peninsula is YES!', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '21', 'Oh, that one\'s for sure.', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '22', 'Yes, but not in the way you imagine.', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '23', 'Yes, yes, a thousand times, yes already!', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '24', 'Yes, now stop pestering me!', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '25', 'The answer will be a yes if you let me out of here.', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '26', 'It\'s as sure as the warts on my backside!', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '27', 'The answer is yes in here, I don\'t see why it would be any different out there.', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '28', 'Three words - \"ab - so - lutely\"!', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '29', 'Yes, but I hoped I would never have to answer that.', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '30', 'Ask me again later - I\'m trying to scratch my nose and it\'s hard to concentrate.', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '31', 'Why should I answer that?  Do you tell fortunes when people shake YOU?', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '32', 'Yes, it will rain.  That\'s not what you asked?  Too bad!', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '33', 'I\'m sorry, I can only speak Demonic.', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '34', 'Yes!  I mean no!  I mean... which answer gets me out of here?', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '35', 'Yes, No, Maybe so.', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '36', 'It won\'t matter, you\'ll be dead by tomorrow.', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '37', 'You should be asking \"Is that rogue behind me going to kill me?\"', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '38', 'It\'s times like these that I wish I had a longer cooldown.', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '39', '%s shrugs. Who knows?', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '40', 'It\'s like my mother always said: [Demonic] \"Razxx khaj jhashxx xashjx.\"', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '41', '%s is ignoring you.', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '42', 'Why me?  Why not a goblin, or a gnome...', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '43', 'What happens in the twisting nether, stays in the twisting nether.', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '44', 'Avoid taking unnecessary gambles. Your lucky numbers are two, two and half, and eleven-teen.', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '45', 'Wouldn\'t you like to know?', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '46', 'Oh, oh, oh!  I can see this one clearly...  Nah, lost it.', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '47', 'This was NOT in my contract!', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '48', 'XRA RAHKI MAZIZRA!', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '49', '4 8 15 16 23 42', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '50', 'Are you making fun of me?', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '51', 'What kind of imp do you think I am?', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '52', 'Say please.', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '53', 'Want to trade places?', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '54', 'Do you ask this question to everything that\'s trapped in a ball?', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '55', 'Hey! You try arranging furniture with some jerk shaking your house!', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '56', 'I can make that happen. Just sign below the dotted line...', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '57', 'Reply hazy and slightly damp. Dry thoroughly and try again.', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '58', 'Concentrate (on releasing me from this infernal prison) and try again later.', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '59', 'Please insert 25 silver pieces and try again.', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '60', 'Are you my pal, Danny?', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '61', 'You remember the time you tried to drill that hole in your head?', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '62', 'Oh that\'s right, don\'t make any effort to make your own fortune!', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '63', 'Two words - imp-possible!', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '64', 'You need Arcane Intellect, because that answer is obvious!  NO!', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '65', 'Not on your life!', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '66', 'I don\'t have to be a fortune-telling imp to know the answer to that one - NO!', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '67', 'The odds are 32.33 (repeating of course) percent chance of success.', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '68', 'When Blackrock freezes over!', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '69', 'Hahahaha, you\'re kidding right?', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '70', 'Inconceivable!', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '71', 'My sources say \"no\". Before the torture, that is.', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '72', 'That\'s about as likely as me getting out of this ball.', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '73', 'You picked the wrong time to shake me today, buddy!  Prepare for disappointment.', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '74', 'Not unless you\'re some kind of super-person.  And don\'t kid yourself, you\'re not.', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '75', 'That\'s about as likely as me getting a date with a succubus.', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '76', 'My fortune telling powers are immeasurable - your chances are though: NO CHANCE', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '77', 'NO - and don\'t try shaking me again for a better answer!', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '78', 'Yes is my answer...', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '79', '...NOT!', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '80', 'I\'m gonna have to give this one the big N-O.', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '81', 'The outlook is very bad - for YOU that is!  Haha, take it!', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '82', 'Survey says: BZZZT!', '15', '0', '100', '0', '0', '0', '');
INSERT INTO `creature_text` VALUES ('14467', '0', '0', 'Kroshius live? Kroshius crush!', '14', '0', '100', '0', '0', '0', 'Kroshius');
INSERT INTO `creature_text` VALUES ('25334', '0', '0', '%s gains fuel.', '41', '0', '100', '0', '0', '0', 'Horde Siege Tank');
INSERT INTO `creature_text` VALUES ('29368', '0', '0', 'How predictable! But then, who would expect a rock to think like anything other than one?', '12', '0', '100', '11', '0', '0', '');
INSERT INTO `creature_text` VALUES ('29368', '1', '0', 'If you\'re so eager to fight, I\'ll oblige you. But know that nothing you do here can prevent the completion of the iron colossus!', '12', '0', '100', '274', '0', '0', '');
INSERT INTO `creature_text` VALUES ('29801', '0', '0', 'At last, the tyranny of the stormforged is at its end!', '12', '0', '100', '25', '0', '0', '');
INSERT INTO `creature_text` VALUES ('29801', '1', '0', 'We\'ve defeated Valduran and we\'ll fell his colossus.', '12', '0', '100', '0', '25', '0', '');
INSERT INTO `creature_text` VALUES ('30152', '0', '0', 'No more will your minions assail the creatures of stone and their allies.', '12', '0', '100', '1', '0', '0', '');
INSERT INTO `creature_text` VALUES ('30152', '1', '0', 'I am your doom, Valduran!', '12', '0', '100', '15', '0', '0', '');
INSERT INTO `creature_text` VALUES ('30152', '2', '0', 'Well fought! The day is ours, but the war goes on!', '12', '0', '100', '4', '0', '0', '');
INSERT INTO `creature_text` VALUES ('27238', '0', '0', 'My beautiful mill. You! You\'ll die for this!', '12', '0', '100', '0', '0', '0', 'Foreman Kaleiki');
INSERT INTO `creature_text` VALUES ('26287', '0', '0', 'Thel\'zan\'s phylactery drops to the ground beneath Icestorm', '41', '0', '100', '0', '0', '0', 'Icestorm');
INSERT INTO `creature_text` VALUES ('27843', '0', '0', 'I\'ll flush her out, $N! You just be ready for her when she comes in!', '12', '0', '100', '1', '0', '0', 'Wyrmbait');
INSERT INTO `creature_text` VALUES ('27844', '0', '0', 'FIRE! FIRE! BRING HER DOWN!', '14', '0', '100', '0', '0', '0', 'Legion Commander Tyralion');
INSERT INTO `creature_text` VALUES ('25802', '0', '0', 'You challenge Kaw, destroyer of mammoths? Then face me and feel my thunder!', '14', '0', '100', '0', '0', '0', 'Kaw the Mammoth Destroyer');
INSERT INTO `creature_text` VALUES ('27383', '0', '0', 'I was... once like you. Mortal. Soft. Only flesh and bone. WEAK AND TIMID!', '14', '0', '100', '0', '0', '0', 'Thel zan the Duskbringer');
INSERT INTO `creature_text` VALUES ('27383', '1', '0', 'Our lord, Arthas saw in me the frailty of man.', '14', '0', '100', '0', '0', '0', 'Thel zan the Duskbringer');
INSERT INTO `creature_text` VALUES ('27383', '2', '0', '\"Serve me in life and I will promise to rend the weakness from your soul. To erase it from existence!\" To be chosen... blessed by the Lich King in such a way.', '14', '0', '100', '0', '0', '0', 'Thel zan the Duskbringer');
INSERT INTO `creature_text` VALUES ('27383', '3', '0', 'So serve him I did... Were it not for me the glorious dread citadel of Naxxramas may never have returned to Northrend.', '14', '0', '100', '0', '0', '0', 'Thel zan the Duskbringer');
INSERT INTO `creature_text` VALUES ('27383', '4', '0', 'But enough talk... You have fought hard and come from the distant reaches of this world to face the Duskbringer. I shall not disappoint...', '14', '0', '100', '0', '0', '0', 'Thel zan the Duskbringer');
INSERT INTO `creature_text` VALUES ('27383', '5', '0', 'Just as our lord saved me, so too shall I save you!', '14', '0', '100', '0', '0', '0', 'Thel zan the Duskbringer');
INSERT INTO `creature_text` VALUES ('27383', '6', '0', 'THIS WILL BE THE LAST TIME YOU UTTER THAT NAME, PALADIN! Breathe your dying breath!', '14', '0', '100', '0', '0', '0', 'Thel zan the Duskbringer');
INSERT INTO `creature_text` VALUES ('27383', '7', '0', 'Rise, my minions! Your master commands it!', '14', '0', '100', '0', '0', '0', 'Thel zan the Duskbringer');
INSERT INTO `creature_text` VALUES ('27857', '0', '0', 'Steel yourselves, soldiers. $N has provided us us with the final piece of this puzzle. The dread lich, Thel\'zan, will soon come out of hiding, only to be rendered powerless against us!', '12', '0', '100', '0', '0', '0', 'Legion Commander Yorik');
INSERT INTO `creature_text` VALUES ('27857', '1', '0', 'It is this phylactery, Thel\'zan\'s phylactery - that is the key to this victory!', '12', '0', '100', '0', '0', '0', 'Legion Commander Yorik');
INSERT INTO `creature_text` VALUES ('27857', '2', '0', 'LEGION, STEADY YOURSELVES!', '12', '0', '100', '0', '0', '0', 'Legion Commander Yorik');
INSERT INTO `creature_text` VALUES ('27858', '0', '0', 'Honor, courage, compassion and justice! Those were once the virtues you fought for as a cleric of the Argent Dawn, Thel\'zan.', '14', '0', '100', '0', '0', '0', 'Highlord Bolvar Fordragon');
INSERT INTO `creature_text` VALUES ('27858', '1', '0', 'You dedicated your whole life to fighting the Scourge. You saw what they did to our beloved Lordaeron. The Lich King took everything from you!', '14', '0', '100', '0', '0', '0', 'Highlord Bolvar Fordragon');
INSERT INTO `creature_text` VALUES ('27858', '2', '0', 'So how, then, did Father Inigo Montoy, bastion of virtue for the Argent Dawn, become the embodiment of that which he hated most?', '12', '0', '100', '0', '0', '0', 'Highlord Bolvar Fordragon');
INSERT INTO `creature_text` VALUES ('27858', '3', '0', 'Now, be free and join the battle! Let us end this together!', '12', '0', '100', '0', '0', '0', 'Highlord Bolvar Fordragon');
INSERT INTO `creature_text` VALUES ('27858', '4', '0', 'Victory for the Alliance!', '14', '0', '100', '0', '0', '0', 'Highlord Bolvar Fordragon');
INSERT INTO `creature_text` VALUES ('27858', '5', '0', 'You have saved Wintergarde! With Thel\'zan\'s filth washed away, the 7th Legion will easily clean up the remaining Scourge. When you are done here, report to Angrathar!', '12', '0', '100', '0', '0', '0', 'Highlord Bolvar Fordragon');
INSERT INTO `creature_text` VALUES ('32236', '1', '1', 'You\'ve got it all wrong. I\'m a subjugator!', '12', '0', '100', '0', '0', '0', 'Dark Subjugator');
INSERT INTO `creature_text` VALUES ('32236', '1', '0', 'It was an accident. I was framed. Don\'t drop me!', '12', '0', '100', '0', '0', '0', 'Dark Subjugator');
INSERT INTO `creature_text` VALUES ('32236', '0', '2', 'What?!', '12', '0', '100', '0', '0', '0', 'Dark Subjugator');
INSERT INTO `creature_text` VALUES ('32236', '0', '1', 'Okay, who\'s the joker that threw an orb at me?', '12', '0', '100', '0', '0', '0', 'Dark Subjugator');
INSERT INTO `creature_text` VALUES ('32236', '0', '0', 'I feel funny all of a sudden. Er?!', '12', '0', '100', '0', '0', '0', 'Dark Subjugator');
INSERT INTO `creature_text` VALUES ('32236', '1', '2', 'NOOOOOO!', '12', '0', '100', '0', '0', '0', 'Dark Subjugator');
INSERT INTO `creature_text` VALUES ('22911', '0', '0', 'You dare summon me?', '14', '0', '100', '15', '0', '0', 'Vim\'gol the Vile');
INSERT INTO `creature_text` VALUES ('22911', '1', '0', 'Now me grow bigger and crush you!', '14', '0', '100', '0', '0', '0', 'Vim\'gol the Vile');
INSERT INTO `creature_text` VALUES ('27247', '0', '0', 'Hey, who rang the bell?', '12', '0', '100', '0', '0', '0', 'Devout Bodyguard');
INSERT INTO `creature_text` VALUES ('27439', '0', '0', 'I know a place nearby where we can speak in private, my child. Follow me.', '12', '0', '100', '1', '0', '0', 'High Abbot Landgren');
INSERT INTO `creature_text` VALUES ('27439', '1', '0', 'Did you think that I could not see through your flimsy disguise, $N?', '12', '0', '100', '6', '0', '0', 'High Abbot Landgren');
INSERT INTO `creature_text` VALUES ('27439', '2', '0', 'There is much that you do not understand, $R. The Master sees all.', '12', '0', '100', '1', '0', '0', 'High Abbot Landgren');
INSERT INTO `creature_text` VALUES ('27439', '3', '0', 'He told me that you would come for me. I won\'t die by your hand, though. I have seen what you have done to my compatriots.', '12', '0', '100', '25', '0', '0', 'High Abbot Landgren');
INSERT INTO `creature_text` VALUES ('27439', '4', '0', 'No. I will leave this world in a manner of my own choosing. And I will return, the grand admiral\'s will permitting!', '12', '0', '100', '274', '0', '0', 'High Abbot Landgren');
INSERT INTO `creature_text` VALUES ('27439', '5', '0', 'AAAEEEEIIIiiiiiiiiiiiiiiiiiiiiiiiiiiii........................................', '12', '0', '100', '0', '0', '2943', 'High Abbot Landgren');
INSERT INTO `creature_text` VALUES ('27492', '1', '0', 'That\'s correct. My men report that the roads from here to the shore are held by the undead. You\'ll need to find an alternate route back to your ships.', '12', '0', '100', '378', '4000', '12724', 'Alliance Emissary 1 - Q: The Truth Shall Set Us Free');
INSERT INTO `creature_text` VALUES ('27492', '0', '0', 'By royal edict, you men are to return to Lordaeron immediately. Lord Uther has convinced the king to recall this expedition.', '12', '0', '100', '25', '3500', '12723', 'Alliance Emissary 0 - Q: The Truth Shall Set Us Free');
INSERT INTO `creature_text` VALUES ('27476', '2', '0', 'To hell with the undead! We\'ll cut our way through the woods, men!', '12', '0', '100', '22', '3000', '12721', 'Valonforth 2  - Q: The Truth Shall Set Us Free');
INSERT INTO `creature_text` VALUES ('27476', '3', '0', 'Well, milord, your father had our troops recalled at Lord Uther\'s request.', '12', '0', '100', '1', '3000', '12722', 'Valonforth 3 - Q: The Truth Shall Set Us Free');
INSERT INTO `creature_text` VALUES ('27476', '0', '0', 'I apologize, emissary, but the prince is away on an errand. Wha brings you to this desolate place?', '12', '0', '100', '1', '3000', '12719', 'Valonforth 0  - Q: The Truth Shall Set Us Free');
INSERT INTO `creature_text` VALUES ('27476', '1', '0', 'We\'re to just pick up and leave?', '12', '0', '100', '6', '1500', '12720', 'Valonforth 1  - Q: The Truth Shall Set Us Free');
INSERT INTO `creature_text` VALUES ('26604', '0', '0', 'Shcourged trooolls roashting....', '12', '0', '100', '396', '0', '0', 'Mack Fearsen');
INSERT INTO `creature_text` VALUES ('26604', '1', '0', '...on an open fire.....', '12', '0', '100', '396', '0', '0', 'Mack Fearsen');
INSERT INTO `creature_text` VALUES ('26604', '2', '0', 'That\'s it! Far as I got... Whatd\'ya think?', '12', '0', '100', '396', '0', '0', 'Mack Fearsen');
INSERT INTO `creature_text` VALUES ('21057', '0', '0', 'Who dares to interrupt my operation?', '14', '0', '100', '0', '0', '0', 'Nexus-Prince Razaan');
INSERT INTO `creature_text` VALUES ('41671', '0', '0', 'I can\'t wait to get home to my family. Thank you, stranger.', '12', '0', '100', '0', '0', '0', 'Trapped Miner - rescued');
INSERT INTO `creature_text` VALUES ('41671', '0', '1', 'I thought I would die down here.', '12', '0', '100', '0', '0', '0', 'Trapped Miner - rescued');
INSERT INTO `creature_text` VALUES ('41671', '0', '2', 'I thought they were going to tear me apart!', '12', '0', '100', '0', '0', '0', 'Trapped Miner - rescued');
INSERT INTO `creature_text` VALUES ('41671', '0', '3', 'Light bless you, stranger. You\'ve saved me!', '12', '0', '100', '0', '0', '0', 'Trapped Miner - rescued');
INSERT INTO `creature_text` VALUES ('41671', '0', '4', 'Thank you!', '12', '0', '100', '0', '0', '0', 'Trapped Miner - rescued');
INSERT INTO `creature_text` VALUES ('41671', '0', '5', 'You\'re the best thing I\'ve seen since the troggs showed up!', '12', '0', '100', '0', '0', '0', 'Trapped Miner - rescued');
INSERT INTO `creature_text` VALUES ('10262', '0', '6', 'Bijou lost! SQUAWK! *cough', '12', '0', '100', '0', '0', '0', 'Opus - Random Say');
INSERT INTO `creature_text` VALUES ('10262', '0', '7', 'Dragons controlling orcs!? SQUAWK!', '12', '0', '100', '0', '0', '0', 'Opus - Random Say');
INSERT INTO `creature_text` VALUES ('29614', '0', '0', 'Even in death there will be no escape for you!', '12', '0', '100', '0', '0', '0', 'Onslaught Darkweaver');
INSERT INTO `creature_text` VALUES ('29614', '0', '1', 'I shall feast upon your soul!', '12', '0', '100', '0', '0', '0', 'Onslaught Darkweaver');
INSERT INTO `creature_text` VALUES ('29614', '0', '2', 'Now you know. Now you must die!', '12', '0', '100', '0', '0', '0', 'Onslaught Darkweaver');
INSERT INTO `creature_text` VALUES ('29614', '0', '3', 'You will not leave this cavern alive!', '12', '0', '100', '0', '0', '0', 'Onslaught Darkweaver');
INSERT INTO `creature_text` VALUES ('29614', '1', '0', 'The darkweaver pierces you with an evil grin as he shifts into an entirely different form.', '16', '0', '100', '0', '0', '0', 'Onslaught Darkweaver');
INSERT INTO `creature_text` VALUES ('14627', '0', '0', 'Now that\'s what ye call heavy hands!', '14', '0', '100', '0', '0', '0', 'Hansel Heavyhands');
INSERT INTO `creature_text` VALUES ('14636', '0', '0', 'Thieves! Scallywags! Rapscallions! Come face me gigantic pillas!', '14', '0', '100', '0', '0', '0', 'Chambermaid Pillaclencher');
INSERT INTO `creature_text` VALUES ('47462', '0', '0', 'The flames of Ragnaros will burn you all.', '14', '0', '100', '0', '0', '0', 'Archduke Calcinder');
INSERT INTO `creature_text` VALUES ('47462', '1', '0', '%s is weakened!  Banish him now!', '41', '0', '100', '0', '0', '0', 'Archduke Calcinder');
INSERT INTO `creature_text` VALUES ('47462', '2', '0', 'Fools!  You cannot defeat me!  I will only return again...', '14', '0', '100', '0', '0', '0', 'Archduke Calcinder');
INSERT INTO `creature_text` VALUES ('47269', '0', '0', 'Why you do that?  Why you kill?', '14', '0', '100', '15', '0', '0', 'Lunk');
INSERT INTO `creature_text` VALUES ('47309', '0', '0', 'You will suffer!  You will face the wrath of the Twilight\'s Hammer!', '12', '0', '100', '0', '0', '0', 'Minister Kiyuubi');
INSERT INTO `creature_text` VALUES ('47310', '0', '0', 'Only pain awaits you!', '12', '0', '100', '1', '0', '0', 'Minister Letherio');
INSERT INTO `creature_text` VALUES ('2755', '0', '0', 'What?  $n, you served me well, but...', '12', '0', '0', '0', '0', '0', 'Myzrael');
INSERT INTO `creature_text` VALUES ('2755', '1', '0', 'Why have you summoned me so soon?  I haven\'t yet reached my full power!', '12', '0', '0', '0', '0', '0', 'Myzrael');
INSERT INTO `creature_text` VALUES ('2755', '2', '0', 'No matter.  You were a fool to help me, and now you will pay!', '12', '0', '0', '0', '0', '0', 'Myzrael');
INSERT INTO `creature_text` VALUES ('3287', '0', '0', 'Go swiftly, $n. My fate is in your hands.', '12', '1', '100', '0', '0', '0', 'Hana zua');
INSERT INTO `creature_text` VALUES ('5734', '0', '0', 'Hmm, it would seem Keever needs a new subject. If that fool Abernathy keeps taking Keever\'s subjects, Keever may have to have a word with him.', '12', '33', '100', '1', '0', '0', 'Apothecary Keever <Royal Apothecary Society>');
INSERT INTO `creature_text` VALUES ('5734', '1', '0', 'Ahh, there we go. Now, Keever must try this vial and see if it works.', '12', '33', '100', '1', '0', '0', 'Apothecary Keever <Royal Apothecary Society>');
INSERT INTO `creature_text` VALUES ('5734', '2', '0', '%s forces the fluid down the weary man\'s throat.', '16', '0', '100', '0', '0', '0', 'Apothecary Keever <Royal Apothecary Society>');
INSERT INTO `creature_text` VALUES ('5734', '3', '0', '%s pokes the small toad.', '16', '0', '100', '0', '0', '0', 'Apothecary Keever <Royal Apothecary Society>');
INSERT INTO `creature_text` VALUES ('5734', '4', '0', 'Not what Keever was hoping for. Keever may have added too much earthroot. Let\'s see if the second serum will do what Keever needs.', '12', '33', '100', '1', '0', '0', 'Apothecary Keever <Royal Apothecary Society>');
INSERT INTO `creature_text` VALUES ('5734', '5', '0', '%s feeds the toad some of the strange liquid.', '16', '0', '100', '0', '0', '0', 'Apothecary Keever <Royal Apothecary Society>');
INSERT INTO `creature_text` VALUES ('5734', '6', '0', '%s pokes the small fuzzy squirrel with obvious disappointment.', '16', '0', '100', '0', '0', '0', 'Apothecary Keever <Royal Apothecary Society>');
INSERT INTO `creature_text` VALUES ('5734', '7', '0', 'Well, that is just not right. The creature is far too small. Let us see what Keever\'s third batch will do.', '12', '33', '100', '1', '0', '0', 'Apothecary Keever <Royal Apothecary Society>');
INSERT INTO `creature_text` VALUES ('5734', '8', '0', '%s feeds the squirrel some of the viscous fluid.', '16', '0', '100', '0', '0', '0', 'Apothecary Keever <Royal Apothecary Society>');
INSERT INTO `creature_text` VALUES ('5734', '9', '0', '%s pokes the skittish rabbit.', '16', '0', '100', '0', '0', '0', 'Apothecary Keever <Royal Apothecary Society>');
INSERT INTO `creature_text` VALUES ('5734', '10', '0', 'Keever is unhappy with this. Perhaps if Keever were to try a larger dose, that may fix this dilemma.', '12', '33', '100', '1', '0', '0', 'Apothecary Keever <Royal Apothecary Society>');
INSERT INTO `creature_text` VALUES ('5734', '11', '0', '%s grabs the rabbit and pours the fluid down its throat, then sets it back inside the cage.', '16', '0', '100', '0', '0', '0', 'Apothecary Keever <Royal Apothecary Society>');
INSERT INTO `creature_text` VALUES ('5734', '12', '0', 'What is this? Did Keever ask for a sheep? Keever wanted a weapon of great power and all he got was this sheep. Keever is very disappointed.', '12', '33', '100', '1', '0', '0', 'Apothecary Keever <Royal Apothecary Society>');
INSERT INTO `creature_text` VALUES ('5734', '13', '0', '%s pokes the wooly sheep repeatedly.', '16', '0', '100', '0', '0', '0', 'Apothecary Keever <Royal Apothecary Society>');
INSERT INTO `creature_text` VALUES ('5734', '14', '0', 'Keever is most pleased.', '12', '33', '100', '11', '0', '0', 'Apothecary Keever <Royal Apothecary Society>');
INSERT INTO `creature_text` VALUES ('36217', '0', '0', 'I\'ve got my eye on you, Faranell.', '12', '1', '100', '1', '0', '0', 'Overseer Kraggosh');
INSERT INTO `creature_text` VALUES ('36217', '1', '0', 'No abominations protecting you. No secrecy. No plotting in the dark.', '12', '1', '100', '0', '0', '0', 'Overseer Kraggosh');
INSERT INTO `creature_text` VALUES ('36217', '2', '0', 'If you so much as spit without my permission, this place shuts down. Forcefully.', '12', '1', '100', '0', '0', '0', 'Overseer Kraggosh');
INSERT INTO `creature_text` VALUES ('2055', '0', '0', 'I assure you, overseer, the Royal Apothecary Society dearly wishes to make up for the tragic misguidance which ended so many lives.', '12', '1', '100', '0', '0', '0', 'Master Apothecary Faranell ');
INSERT INTO `creature_text` VALUES ('2055', '1', '0', 'We will cause you no trouble. We seek only to continue our research in peace.', '12', '1', '100', '0', '0', '0', 'Master Apothecary Faranell ');
INSERT INTO `creature_text` VALUES ('36217', '3', '0', 'We\'ll see about that.', '12', '1', '100', '1', '0', '0', 'Overseer Kraggosh');
INSERT INTO `creature_text` VALUES ('36217', '4', '0', 'Get that rotten nut to shut his trap!', '12', '1', '100', '5', '0', '0', 'Overseer Kraggosh');
INSERT INTO `creature_text` VALUES ('36213', '0', '0', 'Shut your face, you gibbering idiot!', '12', '1', '100', '0', '0', '0', 'Kor\'kron Overseer');
INSERT INTO `creature_text` VALUES ('5697', '0', '0', 'Yes My Lord.', '12', '33', '100', '0', '0', '0', 'Theresa');
INSERT INTO `creature_text` VALUES ('5697', '1', '0', 'My Lord Abernathy sends this to you.', '12', '33', '100', '0', '0', '0', 'Theresa');
INSERT INTO `creature_text` VALUES ('5697', '2', '0', 'My Lord. From Father Lankester.', '12', '33', '100', '0', '0', '0', 'Theresa');
INSERT INTO `creature_text` VALUES ('4607', '0', '0', 'Ugh! What is that scoundrel doing sending his vermin to me? Get out of my sight before I take what is left of your life. And take this with you.', '12', '33', '100', '1', '0', '0', 'Father Lankester');
INSERT INTO `creature_text` VALUES ('5661', '0', '0', 'Edward. Tyler. Prepare for your first challenge.', '12', '33', '100', '1', '0', '0', 'Brother Malach');
INSERT INTO `creature_text` VALUES ('5661', '1', '0', 'Lysta, summon in the captives.', '12', '33', '100', '0', '0', '0', 'Brother Malach');
INSERT INTO `creature_text` VALUES ('5661', '2', '0', 'Not a challenge at all it seems. Let us see how you handle your second test. Lysta, bring forth the minions of the Lich King.', '12', '33', '100', '0', '0', '0', 'Brother Malach');
INSERT INTO `creature_text` VALUES ('5661', '3', '0', 'Lysta, summon in undead captives.', '12', '33', '100', '0', '0', '0', 'Brother Malach');
INSERT INTO `creature_text` VALUES ('5661', '4', '0', 'It is time to face your final challenge young warriors! Prepare for your hardest fight yet.', '12', '33', '100', '0', '0', '0', 'Brother Malach');
INSERT INTO `creature_text` VALUES ('5661', '5', '0', 'Lysta, summon forth... the abomination!', '12', '33', '100', '0', '0', '0', 'Brother Malach');
INSERT INTO `creature_text` VALUES ('5661', '6', '0', 'Well done Edward and Tyler. You are progressing along in your training quite nicely. We shall test your mettle again soon.', '12', '33', '100', '0', '0', '0', 'Brother Malach');
INSERT INTO `creature_text` VALUES ('18303', '0', '0', 'My brothers and sisters. Listen to me. We must speak of the survival of our people. We have narrowly survived one calamity, but I fear the next shall destroy us.', '12', '0', '100', '1', '0', '0', 'Lyria Skystrider');
INSERT INTO `creature_text` VALUES ('18303', '1', '0', 'Can you not see what is happening? Open your eyes. It is laid bare for all to see. What is this new alliance? Why have we betrayed our allies of old, only to take up with the enemies of all that live?', '12', '0', '100', '1', '0', '0', 'Lyria Skystrider');
INSERT INTO `creature_text` VALUES ('18668', '0', '0', 'No, you\'re wrong! We were the ones betrayed!', '12', '0', '100', '22', '0', '0', 'Vaeron Kormar');
INSERT INTO `creature_text` VALUES ('18669', '0', '0', 'When have the dwarves ever been our friends?', '12', '0', '100', '11', '0', '0', 'Terric Brightwind');
INSERT INTO `creature_text` VALUES ('18724', '0', '0', 'The humans abandoned us in our darkest hour! Their prince himself led the assault on our lands.', '12', '0', '100', '25', '0', '0', 'Melaya Tassier');
INSERT INTO `creature_text` VALUES ('18671', '0', '0', 'You fool! The prince\'s minions were the very Forsaken we have now allied ourselves with, and now he has betrayed us!', '12', '0', '100', '5', '0', '0', 'Priest Ennas');
INSERT INTO `creature_text` VALUES ('18668', '1', '0', 'The Ranger-General of Silvermoon will be a loyal ally!', '12', '0', '100', '1', '0', '0', 'Vaeron Kormar');
INSERT INTO `creature_text` VALUES ('18303', '2', '0', 'You speak of her as friend? You are a fool. This \"Dark Lady\" is not Sylvanas.', '12', '0', '100', '1', '0', '0', 'Lyria Skystrider');
INSERT INTO `creature_text` VALUES ('18668', '2', '0', 'Blood will be true. We can trust in none but our own!', '12', '0', '100', '25', '0', '0', 'Vaeron Kormar');
INSERT INTO `creature_text` VALUES ('18303', '3', '0', 'Blood? And these orcs, these fair weather friends? They will aid us?', '12', '0', '100', '11', '0', '0', 'Lyria Skystrider');
INSERT INTO `creature_text` VALUES ('18671', '1', '0', 'A great blight lies across our land! It grows every day. Our need for magic weakens us, and will bring us to destruction, just as it has before!', '12', '0', '100', '25', '0', '0', 'Priest Ennas');
INSERT INTO `creature_text` VALUES ('18669', '1', '0', 'How are we to live without magic?', '12', '0', '100', '5', '0', '0', 'Terric Brightwind');
INSERT INTO `creature_text` VALUES ('18724', '1', '0', 'Look at the wonders of this city! This is magic.', '12', '0', '100', '5', '0', '0', 'Melaya Tassier');
INSERT INTO `creature_text` VALUES ('18671', '2', '0', 'But at what cost? You\'ve all been blinded. We have all been blinded.', '12', '0', '100', '1', '0', '0', 'Priest Ennas');
INSERT INTO `creature_text` VALUES ('18668', '3', '0', 'You call us traitors?', '12', '0', '100', '25', '0', '0', 'Vaeron Kormar');
INSERT INTO `creature_text` VALUES ('18303', '4', '0', 'The magisters are the traitors! They have sealed our doom.', '12', '0', '100', '25', '0', '0', 'Lyria Skystrider');
INSERT INTO `creature_text` VALUES ('18669', '2', '0', 'Idealistic blather. Go back to your forests!', '12', '0', '100', '3', '0', '0', 'Terric Brightwind');
INSERT INTO `creature_text` VALUES ('18724', '2', '0', 'You would have us live like the savages!', '12', '0', '100', '5', '0', '0', 'Melaya Tassier');
INSERT INTO `creature_text` VALUES ('18671', '3', '0', 'No, not savagery! Conscience. The excesses of our people will destroy us, and all that we have struggled to build.', '12', '0', '100', '1', '0', '0', 'Priest Ennas');
INSERT INTO `creature_text` VALUES ('18303', '5', '0', 'We have not struggled so hard to survive, only to bring about the true destruction.', '12', '0', '100', '1', '0', '0', 'Lyria Skystrider');
INSERT INTO `creature_text` VALUES ('18671', '4', '0', 'We stand on the brink of destruction. We must reconsider this rash course of action.', '12', '0', '100', '5', '0', '0', 'Priest Ennas');
INSERT INTO `creature_text` VALUES ('18303', '6', '0', 'If we speak as one, we cannot be ignored!', '12', '0', '100', '1', '0', '0', 'Lyria Skystrider');
INSERT INTO `creature_text` VALUES ('18671', '5', '0', 'Lend us your voices! Now is the time we must act to save our people.', '12', '0', '100', '1', '0', '0', 'Priest Ennas');
INSERT INTO `creature_text` VALUES ('18671', '6', '0', 'Lor\'themar must hear us! We will not be silenced!', '12', '0', '100', '5', '0', '0', 'Priest Ennas');
INSERT INTO `creature_text` VALUES ('18337', '0', '0', 'That should take care of that. Summon me if you have further need of my services.', '12', '0', '100', '11', '0', '0', 'Priest Kath\'mar');
INSERT INTO `creature_text` VALUES ('18303', '7', '0', 'We will repay the betrayal of the Alliance! We shall never forget, and never forgive!', '12', '1', '100', '21', '0', '0', 'Lyria Skystrider');
INSERT INTO `creature_text` VALUES ('18671', '7', '0', 'Lor\'themar has remade Silvermoon into something we may truly be proud of.', '12', '1', '100', '21', '0', '0', 'Priest Ennas');
INSERT INTO `creature_text` VALUES ('18671', '8', '0', 'The magisters are wise and will plot a prosperous course for our future.', '12', '1', '100', '21', '0', '0', 'Priest Ennas');
INSERT INTO `creature_text` VALUES ('18896', '0', '0', 'Function: Intelligence Agents, Torturers', '12', '7', '100', '0', '0', '0', 'Exodar Holographic Emitter');
INSERT INTO `creature_text` VALUES ('18896', '1', '0', 'The cruel sayaadi temptresses exemplify the subtler side of the Legion\'s nihilistic Crusade.', '12', '7', '100', '0', '0', '0', 'Exodar Holographic Emitter');
INSERT INTO `creature_text` VALUES ('18896', '2', '0', 'They revel in causing anguish and pain - and their murderous interrogations are the stuff of nightmares.', '12', '7', '100', '0', '0', '0', 'Exodar Holographic Emitter');
INSERT INTO `creature_text` VALUES ('18896', '3', '0', 'Felguard', '12', '7', '100', '0', '0', '0', 'Exodar Holographic Emitter');
INSERT INTO `creature_text` VALUES ('18896', '4', '0', 'Species: Mo\'arg', '12', '7', '100', '0', '0', '0', 'Exodar Holographic Emitter');
INSERT INTO `creature_text` VALUES ('18896', '5', '0', 'Affiliation: Burning Legion', '12', '7', '100', '0', '0', '0', 'Exodar Holographic Emitter');
INSERT INTO `creature_text` VALUES ('18896', '6', '0', 'Function: Standard Soldiers', '12', '7', '100', '0', '0', '0', 'Exodar Holographic Emitter');
INSERT INTO `creature_text` VALUES ('18896', '7', '0', 'Conscripted from the mo\'arg race, these warriors serve as the Legion\'s rank and file soldiers.', '12', '7', '100', '0', '0', '0', 'Exodar Holographic Emitter');
INSERT INTO `creature_text` VALUES ('18896', '8', '0', 'They are fearless and unwaveringly loyal to their master\'s godlike will.', '12', '7', '100', '0', '0', '0', 'Exodar Holographic Emitter');
INSERT INTO `creature_text` VALUES ('18896', '9', '0', 'Doomguard', '12', '7', '100', '0', '0', '0', 'Exodar Holographic Emitter');
INSERT INTO `creature_text` VALUES ('18896', '10', '0', 'Species: Ered\'ruin', '12', '7', '100', '0', '0', '0', 'Exodar Holographic Emitter');
INSERT INTO `creature_text` VALUES ('18896', '11', '0', 'Function: Shock Troops, Enforcers', '12', '7', '100', '0', '0', '0', 'Exodar Holographic Emitter');
INSERT INTO `creature_text` VALUES ('18896', '12', '0', 'Powerful and deliberate, these winged aberrations are renowned across the cosmos for their brutality.', '12', '7', '100', '0', '0', '0', 'Exodar Holographic Emitter');
INSERT INTO `creature_text` VALUES ('18896', '13', '0', 'The Doomguard are utterly devoted to their Pit Lord masters and revel in the destruction of worlds.', '12', '7', '100', '0', '0', '0', 'Exodar Holographic Emitter');
INSERT INTO `creature_text` VALUES ('18896', '14', '0', 'Wrathguard', '12', '7', '100', '0', '0', '0', 'Exodar Holographic Emitter');
INSERT INTO `creature_text` VALUES ('18896', '15', '0', 'Species: Eredar', '12', '7', '100', '0', '0', '0', 'Exodar Holographic Emitter');
INSERT INTO `creature_text` VALUES ('18896', '16', '0', 'Function: Honor Guard', '12', '7', '100', '0', '0', '0', 'Exodar Holographic Emitter');
INSERT INTO `creature_text` VALUES ('18896', '17', '0', 'Cunning and precise, the Wrathguard often protect the Legion\'s eredar masters.', '12', '7', '100', '0', '0', '0', 'Exodar Holographic Emitter');
INSERT INTO `creature_text` VALUES ('18896', '18', '0', 'Their skill in battle should never be underestimated.', '12', '7', '100', '0', '0', '0', 'Exodar Holographic Emitter');
INSERT INTO `creature_text` VALUES ('18896', '19', '0', 'Succubus', '12', '7', '100', '0', '0', '0', 'Exodar Holographic Emitter');
INSERT INTO `creature_text` VALUES ('18896', '20', '0', 'Species: Sayaad', '12', '7', '100', '0', '0', '0', 'Exodar Holographic Emitter');
INSERT INTO `creature_text` VALUES ('18896', '21', '0', 'Shivarra', '12', '7', '100', '0', '0', '0', 'Exodar Holographic Emitter');
INSERT INTO `creature_text` VALUES ('18896', '22', '0', 'Species: Shivarra', '12', '7', '100', '0', '0', '0', 'Exodar Holographic Emitter');
INSERT INTO `creature_text` VALUES ('18896', '23', '0', 'Function: Priestesses, Military Chaplains', '12', '7', '100', '0', '0', '0', 'Exodar Holographic Emitter');
INSERT INTO `creature_text` VALUES ('18896', '24', '0', 'Devout and exceedingly charismatic, these priestesses live only to evangelize the power of Sargeras\' dread vision.', '12', '7', '100', '0', '0', '0', 'Exodar Holographic Emitter');
INSERT INTO `creature_text` VALUES ('18896', '25', '0', 'They are the driving heart of the Burning Crusade.', '12', '7', '100', '0', '0', '0', 'Exodar Holographic Emitter');
INSERT INTO `creature_text` VALUES ('18896', '26', '0', 'Dreadlord', '12', '7', '100', '0', '0', '0', 'Exodar Holographic Emitter');
INSERT INTO `creature_text` VALUES ('18896', '27', '0', 'Species: Nathrezim', '12', '7', '100', '0', '0', '0', 'Exodar Holographic Emitter');
INSERT INTO `creature_text` VALUES ('18896', '28', '0', 'Function: Secret Police, Interrogator', '12', '7', '100', '0', '0', '0', 'Exodar Holographic Emitter');
INSERT INTO `creature_text` VALUES ('18896', '29', '0', 'The Nathrezim are merciless villains who feed upon the energies of mortal creatures.', '12', '7', '100', '0', '0', '0', 'Exodar Holographic Emitter');
INSERT INTO `creature_text` VALUES ('18896', '30', '0', 'They utilize terror and subterfuge - often turning brother against brother as whole worlds fall before their dark influence.', '12', '7', '100', '0', '0', '0', 'Exodar Holographic Emitter');
INSERT INTO `creature_text` VALUES ('18896', '31', '0', 'Eredar', '12', '7', '100', '0', '0', '0', 'Exodar Holographic Emitter');
INSERT INTO `creature_text` VALUES ('18896', '32', '0', 'Function: Overlords, Strategists', '12', '7', '100', '0', '0', '0', 'Exodar Holographic Emitter');
INSERT INTO `creature_text` VALUES ('18896', '33', '0', 'The cunning, powerful eredar warlocks were once our noble kinsmen.', '12', '7', '100', '0', '0', '0', 'Exodar Holographic Emitter');
INSERT INTO `creature_text` VALUES ('18896', '34', '0', 'Yet they fell to the lure of Sargeras\' evil - and now they have become our most stalwart enemies.', '12', '7', '100', '0', '0', '0', 'Exodar Holographic Emitter');
