INSERT INTO `creature_text` VALUES ('24123', '1', '0', 'I think I\'ve found the one I\'ll use. Now I have to begin the task of carving it. I don\'t think it\'s going to be easy. I don\'t even know what a female yeti\'s mating call sounds like!', '12', '0', '100', '396', '0', '0', 'Nokoma Snowseer');
INSERT INTO `creature_text` VALUES ('24123', '2', '0', 'I\'ve got most of the basic carving done, but it needs to be tested before I can continue. Are you willing to help me with that, $n?', '12', '0', '100', '396', '0', '0', 'Nokoma Snowseer');
