INSERT INTO `creature_text` VALUES ('24362', '0', '0', 'They keep their tools in boxes that look like this. Look for them near the pavilions.', '12', '0', '100', '25', '0', '0', 'Longrunner Pembe');
INSERT INTO `creature_text` VALUES ('24362', '1', '0', 'I\'m only going to carve the runes I think will give you command over a giant.', '12', '0', '100', '1', '0', '0', 'Longrunner Pembe');
INSERT INTO `creature_text` VALUES ('24362', '2', '0', 'I\'m omitting the runes for \"north\" and \"west\" because I\'m not sure how they impact the giant. I\'m also including a rune to limit how long you can control it.', '12', '0', '100', '0', '0', '0', 'Longrunner Pembe');
INSERT INTO `creature_text` VALUES ('24362', '3', '0', 'There we go, the Rune of Command... hopefully. Not nearly as vicious as its Iron Dwarf cousin, but functional.', '12', '0', '100', '1', '0', '0', 'Longrunner Pembe');
INSERT INTO `creature_text` VALUES ('27210', '0', '0', 'Now I\'ll show you the REAL power of the Onslaught!', '12', '0', '100', '0', '0', '14192', 'High General Abbendis');
INSERT INTO `creature_text` VALUES ('27951', '0', '0', 'Kill them Abbendis! Do not fail me!', '14', '0', '100', '25', '0', '14198', 'Admiral Barean Westwind');
INSERT INTO `creature_text` VALUES ('27951', '1', '0', 'The grand admiral studies you a moment through narrowed eyes.', '16', '0', '100', '0', '0', '0', 'Admiral Barean Westwind');
INSERT INTO `creature_text` VALUES ('27951', '2', '0', 'You served your purpose.', '12', '0', '100', '0', '0', '14199', 'Admiral Barean Westwind');
INSERT INTO `creature_text` VALUES ('27951', '3', '0', 'I will see you in Icecrown, weakling... if you live that long!', '12', '0', '100', '0', '0', '14200', 'Admiral Barean Westwind');
