INSERT INTO `creature_text` VALUES ('34044', '0', '0', 'Just in time. Let\'s see this thing off, then.', '12', '0', '100', '0', '0', '15826', 'Brann Bronzebeard');
INSERT INTO `creature_text` VALUES ('16128', '0', '0', 'We received Brann\'s message, and we have begun preparations.', '12', '0', '100', '1', '0', '15649', 'Rhonin');
INSERT INTO `creature_text` VALUES ('16128', '1', '0', 'Citizens of Dalaran! Raise your eyes to the skies and observe!', '14', '0', '100', '0', '0', '15650', 'Rhonin');
INSERT INTO `creature_text` VALUES ('16128', '2', '0', 'Today our world\'s destruction has been averted in defiance of our very makers!', '14', '0', '100', '0', '0', '15651', 'Rhonin');
INSERT INTO `creature_text` VALUES ('16128', '3', '0', 'Algalon the Observer, herald of the titans, has been defeated by our brave comrades in the depths of the titan city of Ulduar.', '14', '0', '100', '0', '0', '15652', 'Rhonin');
INSERT INTO `creature_text` VALUES ('16128', '4', '0', 'Algalon was sent here to judge the fate of our world.', '14', '0', '100', '0', '0', '15653', 'Rhonin');
INSERT INTO `creature_text` VALUES ('16128', '5', '0', 'He found a planet whose races had deviated from the titans\' blueprints. A planet where not everything had gone according to plan.', '14', '0', '100', '0', '0', '15654', 'Rhonin');
INSERT INTO `creature_text` VALUES ('16128', '6', '0', 'Cold logic deemed our world not worth saving. Cold logic, however, does not account for the power of free will. It\'s up to each of us to prove this is a world worth saving.', '14', '0', '100', '0', '0', '15655', 'Rhonin');
INSERT INTO `creature_text` VALUES ('16128', '7', '0', 'That our lives... our lives are worth living.', '14', '0', '100', '0', '0', '15656', 'Rhonin');
INSERT INTO `creature_text` VALUES ('28092', '0', '0', 'Omega Rune deployed.  Activation successful.', '12', '0', '100', '51', '0', '0', 'The Etymidian');
INSERT INTO `creature_text` VALUES ('28033', '0', '0', 'Finally, we have the parts to repair the Spirit of Gnomeregan.', '12', '0', '100', '1', '0', '0', 'Weslex Quickwrench');
INSERT INTO `creature_text` VALUES ('28033', '1', '0', 'Let\'s see, just a tweak here and an adjustment there...', '12', '0', '100', '432', '0', '0', 'Weslex Quickwrench');
INSERT INTO `creature_text` VALUES ('28033', '2', '0', 'And there she is, the Spirit of Gnomeregan!', '12', '0', '100', '25', '0', '0', 'Weslex Quickwrench');
INSERT INTO `creature_text` VALUES ('28033', '3', '0', 'You can use her, but I don\'t want to hear any complaints about saw blades in your seat. I had to fix \'er with what we could find.', '12', '0', '100', '1', '0', '0', 'Weslex Quickwrench');
INSERT INTO `creature_text` VALUES ('28998', '0', '0', 'Behold, mon! We be creatin\' da greatest Scourge army ever seen!', '12', '0', '100', '396', '0', '14023', 'Overlord Drakuru');
INSERT INTO `creature_text` VALUES ('28998', '1', '0', 'These be da first of many! We shall sweep across Zul\'Drak like da wind....', '12', '0', '100', '396', '0', '14024', 'Overlord Drakuru');
INSERT INTO `creature_text` VALUES ('28998', '2', '0', 'What treachery is this?! You be payin\' for this deceit with your life, mon!', '12', '0', '100', '0', '0', '14025', 'Overlord Drakuru');
INSERT INTO `creature_text` VALUES ('28998', '3', '0', 'The Scepter of Domination glows brightly! Use it now!', '41', '0', '100', '0', '0', '0', 'Overlord Drakuru');
INSERT INTO `creature_text` VALUES ('28998', '4', '0', 'How could dis be? Da Lich King said he be havin\' great plans for you, mon...', '14', '0', '100', '0', '0', '14027', 'Overlord Drakuru');
INSERT INTO `creature_text` VALUES ('28998', '4', '1', 'Ya done turned your back on destiny, mon. Now you gunna die!', '14', '0', '100', '0', '0', '14029', 'Overlord Drakuru');
INSERT INTO `creature_text` VALUES ('28998', '4', '2', 'Fool! Ya coulda been havin\' great power, mon!', '14', '0', '100', '0', '0', '14026', 'Overlord Drakuru');
INSERT INTO `creature_text` VALUES ('28998', '4', '3', 'Your betrayal not gunna be slowin\' me down none, mon.', '14', '0', '100', '0', '0', '14030', 'Overlord Drakuru');
INSERT INTO `creature_text` VALUES ('28998', '4', '4', 'I shoulda known not to be trustin\' nobody!', '14', '0', '100', '0', '0', '14028', 'Overlord Drakuru');
INSERT INTO `creature_text` VALUES ('28998', '4', '5', 'I never needed ya, mon. I can crush Zul\'Drak without ya!', '14', '0', '100', '0', '0', '14031', 'Overlord Drakuru');
INSERT INTO `creature_text` VALUES ('28998', '5', '0', 'ENOUGH!', '14', '0', '100', '22', '0', '14020', 'Overlord Drakuru');
INSERT INTO `creature_text` VALUES ('28998', '6', '0', 'Dis foolish treachery has cost ya your destiny!', '14', '0', '100', '396', '0', '14021', 'Overlord Drakuru');
INSERT INTO `creature_text` VALUES ('28998', '7', '0', 'Master, dis mortal scum be double-crossin\' us. Dey must be made to suffer!', '12', '0', '100', '396', '0', '14022', 'Overlord Drakuru');
INSERT INTO `creature_text` VALUES ('28998', '8', '0', 'Disguise Failing! Avoid Scourge Contact!', '41', '0', '100', '0', '0', '0', 'Overlord Drakuru');
INSERT INTO `creature_text` VALUES ('28479', '0', '0', 'Gods of old, we beseech you. What must we do? How can we save the gods of this land?', '15', '0', '100', '6', '0', '0', 'Witch Doctor Khufu');
