INSERT INTO `creature_text` VALUES ('28516', '0', '0', 'But she is chained. A way must be found to free her.', '12', '0', '100', '0', '0', '12196', 'Akil\'zon');
INSERT INTO `creature_text` VALUES ('28517', '0', '0', 'To her altar this mortal must travel. What they must do will not be easy. In the end it may prove impossible.', '12', '0', '100', '0', '0', '643', 'Halazzi');
