INSERT INTO `creature_text` VALUES ('14848', '58', '0', 'The Iceblood Graveyard is under attack!  If left unchecked, the Horde will capture it!', '14', '0', '100', '0', '0', '8174', 'Herald');
INSERT INTO `creature_text` VALUES ('14848', '59', '0', 'The Iceblood Graveyard was taken by the Alliance!', '14', '0', '100', '0', '0', '8173', 'Herald');
INSERT INTO `creature_text` VALUES ('14848', '60', '0', 'The Iceblood Graveyard is under attack!  If left unchecked, the Alliance will capture it!', '14', '0', '100', '0', '0', '8212', 'Herald');
INSERT INTO `creature_text` VALUES ('14848', '61', '0', 'The Alliance has taken Coldtooth Mine!  Its supplies will now be used for reinforcements!', '14', '0', '100', '0', '0', '8173', 'Herald');
INSERT INTO `creature_text` VALUES ('14848', '62', '0', 'The Alliance has taken the Irondeep Mine!  Its supplies will now be used for reinforcements!', '14', '0', '100', '0', '0', '8173', 'Herald');
INSERT INTO `creature_text` VALUES ('14848', '63', '0', 'The Horde has taken the Coldtooth Mine!  Its supplies will now be used for reinforcements!', '14', '0', '100', '0', '0', '8213', 'Herald');
INSERT INTO `creature_text` VALUES ('14848', '64', '0', 'The Horde has taken the Irondeep Mine!  Its supplies will now be used for reinforcements!', '14', '0', '100', '0', '0', '8213', 'Herald');
INSERT INTO `creature_text` VALUES ('14848', '65', '0', 'The Frostwolf General is dead!', '14', '0', '100', '0', '0', '0', 'Herald');
INSERT INTO `creature_text` VALUES ('14848', '66', '0', 'The Stormpike General is dead!', '14', '0', '100', '0', '0', '0', 'Herald');
INSERT INTO `creature_text` VALUES ('14848', '67', '0', 'The Alliance wins!', '14', '0', '100', '0', '0', '0', 'Herald');
INSERT INTO `creature_text` VALUES ('14848', '68', '0', 'The Horde wins!', '14', '0', '100', '0', '0', '0', 'Herald');
INSERT INTO `creature_text` VALUES ('35361', '0', '0', 'Welcome, Warchief Thrall. Overlord Hellscream.', '12', '0', '100', '2', '0', '16056', 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES ('35361', '1', '0', 'I hope you\'ll see the merit of these events in time, Garrosh. We cannot win against the Scourge if we continue to war against one another.', '12', '0', '100', '0', '0', '16057', 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES ('35361', '2', '0', 'Under my roof, gentlemen, I trust you will behave.', '12', '0', '100', '0', '0', '16058', 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES ('35361', '3', '0', 'If you will follow me, then.', '12', '0', '100', '1', '0', '16059', 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES ('35372', '0', '0', 'Pah! That was a show of strength, worthy of a leader. I only regret I did not kill that human before the mage interfered.', '12', '0', '100', '5', '0', '16027', 'Garrosh Hellscream');
INSERT INTO `creature_text` VALUES ('35372', '1', '0', 'What honor is there in thrashing about with blunted sticks? This is a waste of time.', '12', '0', '100', '1', '0', '16028', 'Garrosh Hellscream');
INSERT INTO `creature_text` VALUES ('35372', '2', '0', 'Speak for yourself, Thrall.', '12', '0', '100', '1', '0', '16029', 'Garrosh Hellscream');
INSERT INTO `creature_text` VALUES ('35372', '3', '0', 'Words of a fool, you mean. The Horde will destroy the undead without your aid, human, or that of the pompous king.', '12', '0', '100', '274', '0', '16030', 'Garrosh Hellscream');
INSERT INTO `creature_text` VALUES ('35372', '4', '0', 'Bah.', '12', '0', '100', '1', '0', '16031', 'Garrosh Hellscream');
INSERT INTO `creature_text` VALUES ('35368', '0', '0', 'Garrosh, I expect you to control yourself here. I do not want a repeat of the Violet Citadel.', '12', '0', '100', '1', '0', '16181', 'Thrall');
INSERT INTO `creature_text` VALUES ('35368', '1', '0', 'We are guests here, Garrosh, and you will conduct yourself honorably.', '12', '0', '100', '0', '0', '16182', 'Thrall');
INSERT INTO `creature_text` VALUES ('35368', '2', '0', 'Thank you for the invitation, Lord Fordring. We look forward to observing these games.', '12', '0', '100', '66', '0', '16183', 'Thrall');
INSERT INTO `creature_text` VALUES ('35368', '3', '0', 'Wise words.', '12', '0', '100', '1', '0', '16184', 'Thrall');
INSERT INTO `creature_text` VALUES ('35368', '4', '0', 'Garrosh, enough!', '12', '0', '100', '5', '0', '16185', 'Thrall');
INSERT INTO `creature_text` VALUES ('35368', '5', '0', 'Of course, Tirion. I apologize for his outburst.  It will not be repeated.', '12', '0', '100', '0', '0', '16186', 'Thrall');
INSERT INTO `creature_text` VALUES ('35361', '4', '0', 'King Varian, Lady Jaina. Welcome to the tournament. Your place in the coliseum is waiting.', '12', '0', '100', '1', '0', '16054', 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES ('35361', '5', '0', 'My people are here to ensure they honor the rules of engagement. It will do your subjects well to see you in the stands.', '12', '0', '100', '1', '0', '16055', 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES ('35361', '6', '0', '%s nods and gestures for his guests to follow.', '16', '0', '100', '273', '0', '0', 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES ('35321', '0', '0', 'Tirion.', '12', '0', '100', '1', '0', '16070', 'King Varian Wrynn');
INSERT INTO `creature_text` VALUES ('35321', '1', '0', 'You ask much of me, Tirion, to sit and watch, trusting the savages to keep themselves in check.', '12', '0', '100', '1', '0', '16071', 'King Varian Wrynn');
INSERT INTO `creature_text` VALUES ('35321', '2', '0', 'Perhaps. I doubt the effectiveness of this gambit. We would be better served preparing our armies for the final assault.', '12', '0', '100', '1', '0', '16072', 'King Varian Wrynn');
INSERT INTO `creature_text` VALUES ('35321', '3', '0', 'But if my presence is required in these games to gain your support in the battle, then I will remain.  For now.', '12', '0', '100', '1', '0', '0', 'King Varian Wrynn');
INSERT INTO `creature_text` VALUES ('35320', '0', '0', 'Thank you. It\'s the least I can do to support this; we need to stand together.', '12', '0', '100', '2', '0', '16120', 'Lady Jaina Proudmoore');
INSERT INTO `creature_text` VALUES ('35320', '1', '0', 'I\'m sure Thrall will have his people under control...', '12', '0', '100', '274', '0', '16121', 'Lady Jaina Proudmoore');
INSERT INTO `creature_text` VALUES ('35361', '7', '0', 'Combatants of the Alliance and Horde! You are welcome under the banner of the Argent Crusade.', '12', '0', '100', '5', '0', '0', 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES ('35361', '8', '0', 'To the south lies our goal. We will march to the Citadel and cut out the heart of the Scourge where it dwells.', '12', '0', '100', '25', '0', '0', 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES ('35361', '9', '0', 'But this is no task to be taken lightly.', '12', '0', '100', '0', '0', '0', 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES ('35361', '10', '0', 'A massive attack with every able-bodied man would end in needless slaughter. Every soldier lost would rise as the enemy. Azeroth would be left defenseless against the undead threat.', '12', '0', '100', '0', '0', '0', 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES ('35361', '11', '0', 'Instead, we require a small, concentrated strike force for the attack to succeed. For that reason, we have created the Argent Tournament.', '12', '0', '100', '0', '0', '0', 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES ('35361', '12', '0', 'Within these walls, you will be tested. Your skill in combat will be matched against the fiercest dangers Northrend has to offer under the watchful eyes of your leaders.', '12', '0', '100', '0', '0', '0', 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES ('35361', '13', '0', 'Your prowess, your might, and your cunning will be under close watch. These games will determine the best Azeroth has to offer.', '12', '0', '100', '0', '0', '0', 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES ('35361', '14', '0', 'The victors will take their rightful place in the assault upon the Citadel. We will stand together in the face of evil, and Arthas WILL fall!', '12', '0', '100', '0', '0', '0', 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES ('35501', '0', '0', 'The Warchief of the Horde, Thrall, and Overlord Garrosh Hellscream have arrived upon the tournament grounds! Clear the way to the entrance!', '14', '0', '100', '22', '0', '0', 'Herald Beluus');
INSERT INTO `creature_text` VALUES ('35501', '1', '0', 'Highlord Tirion Fordring approaches the stage to address those gathered upon the tournament grounds! Make way to the stage area to attend him!', '14', '0', '100', '22', '0', '0', 'Herald Beluus');
INSERT INTO `creature_text` VALUES ('35501', '2', '0', 'His Majesty, King Varian Wrynn, and Lady Jaina Proudmoore have touched down upon the tournament grounds! Make way!', '14', '0', '100', '22', '0', '0', 'Herald Beluus');
INSERT INTO `creature_text` VALUES ('33417', '0', '0', 'Now, I will imbue this crystal with the Light\'s power to cut through lies and deceit.', '12', '0', '100', '1', '0', '0', 'Crusader Rhydalla');
INSERT INTO `creature_text` VALUES ('33417', '1', '0', 'Grant us the ability to know the truth. If a murderer walks in our midst, guide us in our search for justice.', '12', '0', '100', '1', '0', '0', 'Crusader Rhydalla');
INSERT INTO `creature_text` VALUES ('48344', '0', '0', 'KROSHIUS CRUSH!', '14', '0', '100', '0', '0', '0', 'Kroshius');
INSERT INTO `creature_text` VALUES ('48332', '0', '0', 'If you\'ve been wrasslin\' with oil, be sure to avoid all sources of sparks or open flame, such as candles, lighters, mages, Kester\'s shop, and Deputy Clunky!', '14', '0', '100', '0', '0', '0', 'Deputy Clunky');
INSERT INTO `creature_text` VALUES ('48332', '0', '1', 'Always wear head protection while on the job! You never know when Miss Darla might have to smack you in the face!', '14', '0', '100', '0', '0', '0', 'Deputy Clunky');
INSERT INTO `creature_text` VALUES ('48332', '0', '2', 'When you\'re doggone tired, it\'s mighty easy to slip up on the job, pardner! Be sure to get at least two hours\' rest between shifts!', '14', '0', '100', '0', '0', '0', 'Deputy Clunky');
INSERT INTO `creature_text` VALUES ('19228', '0', '4', 'Whoa!  Is all that applause for me?  Thanks for coming out!', '12', '0', '100', '6', '0', '0', 'Perry Gatner');
INSERT INTO `creature_text` VALUES ('19228', '47', '0', 'Used to be you couldn\'t trust a goblin...', '12', '0', '100', '1', '0', '0', 'Perry Gatner');
INSERT INTO `creature_text` VALUES ('19228', '48', '0', 'Who am I kiddin\'? If you trade with goblins you\'d better have iron plating in your coinpurse.', '12', '0', '100', '6', '0', '0', 'Perry Gatner');
INSERT INTO `creature_text` VALUES ('18166', '0', '0', 'Do not burden A\'dal with mundane questions, $r.  This being\'s will is all that keeps our enemies from crushing this city.', '12', '0', '100', '0', '0', '0', 'Khadgar');
INSERT INTO `creature_text` VALUES ('18166', '1', '0', 'Show our guest around Shattrath, will you?  Keep an eye out for pickpockets in the Lower City.', '12', '0', '100', '0', '0', '0', 'Khadgar');
INSERT INTO `creature_text` VALUES ('26527', '0', '0', 'Oh, no! Adventurers, something awful has happened! A colleague of mine has been captured by the Infinite Dragonflight, and they\'re doing something horrible to him! Keeping Arthas is still your highest priority, but if you act fast you could help save a Guardian of Time!', '15', '0', '100', '0', '0', '0', 'Chromie');
INSERT INTO `creature_text` VALUES ('26527', '1', '0', 'Adventurers, you must hurry! The Guardian of Time cannot last for much longer!', '15', '0', '100', '0', '0', '0', 'Chromie');
INSERT INTO `creature_text` VALUES ('26527', '2', '0', 'I can barely sense the Guardian of Time! His timeline is fading quickly!', '15', '0', '100', '0', '0', '0', 'Chromie');
INSERT INTO `creature_text` VALUES ('47339', '0', '0', 'Go away, kid, you\'re botherin\' me!', '12', '0', '100', '0', '0', '0', 'Impsy');
INSERT INTO `creature_text` VALUES ('47339', '1', '0', 'Right in the face! Ow ow ow...', '14', '0', '100', '0', '0', '0', 'Impsy');
INSERT INTO `creature_text` VALUES ('47339', '2', '0', 'Catch Impsy in the sack!', '41', '0', '100', '0', '0', '0', 'Impsy');
INSERT INTO `creature_text` VALUES ('47366', '0', '0', 'I\'ll talk, I\'ll talk! Anything! Just keep that perfumed bag away from me!', '12', '0', '100', '0', '0', '0', 'Impsy');
INSERT INTO `creature_text` VALUES ('47369', '0', '0', '%s flees in terror!', '16', '0', '100', '0', '0', '0', 'Jadefire Imp to Rainbow! - Channeller');
INSERT INTO `creature_text` VALUES ('47369', '0', '1', '%s screams at the top of its lungs!', '16', '0', '100', '0', '0', '0', 'Jadefire Imp to Rainbow! - Channeller');
INSERT INTO `creature_text` VALUES ('47369', '0', '2', '%s starts sobbing uncontrollably!', '16', '0', '100', '0', '0', '0', 'Jadefire Imp to Rainbow! - Channeller');
INSERT INTO `creature_text` VALUES ('47369', '1', '0', 'The colors! Nooooooooooooooo!', '14', '0', '100', '0', '0', '0', 'Jadefire Imp to Rainbow! - Channeller');
INSERT INTO `creature_text` VALUES ('47369', '1', '1', 'AAAAAAAAAAAAHHH!!', '14', '0', '100', '0', '0', '0', 'Jadefire Imp to Rainbow! - Channeller');
INSERT INTO `creature_text` VALUES ('47369', '1', '2', 'It burns! IT BURNS!', '14', '0', '100', '0', '0', '0', 'Jadefire Imp to Rainbow! - Channeller');
INSERT INTO `creature_text` VALUES ('47369', '1', '3', 'It\'s so intense!', '14', '0', '100', '0', '0', '0', 'Jadefire Imp to Rainbow! - Channeller');
INSERT INTO `creature_text` VALUES ('47369', '1', '4', 'It\'s too much!', '14', '0', '100', '0', '0', '0', 'Jadefire Imp to Rainbow! - Channeller');
INSERT INTO `creature_text` VALUES ('47369', '1', '5', 'Get it away!', '14', '0', '100', '0', '0', '0', 'Jadefire Imp to Rainbow! - Channeller');
INSERT INTO `creature_text` VALUES ('47369', '1', '6', 'Make it stop! MAKE IT STOP!', '14', '0', '100', '0', '0', '0', 'Jadefire Imp to Rainbow! - Channeller');
INSERT INTO `creature_text` VALUES ('47308', '0', '0', 'BEES! Jump in water to get them off!', '42', '0', '100', '0', '0', '0', 'Felwood Honey Bunny');
INSERT INTO `creature_text` VALUES ('47556', '0', '0', 'Hee hee... hee hee hee... ha ha ha!', '12', '0', '100', '0', '0', '0', 'Drizle');
INSERT INTO `creature_text` VALUES ('47556', '1', '0', 'Don\'t call me dummy, dummy!', '12', '0', '100', '0', '0', '0', 'Drizle');
INSERT INTO `creature_text` VALUES ('47556', '2', '0', 'Run!', '12', '0', '100', '0', '0', '0', 'Drizle');
INSERT INTO `creature_text` VALUES ('47558', '0', '0', 'Shh! They can hear you, dummy!', '12', '0', '100', '0', '0', '0', 'Ferli');
INSERT INTO `creature_text` VALUES ('47558', '1', '0', 'Ow! Now they can see us!', '12', '0', '100', '0', '0', '0', 'Ferli');
INSERT INTO `creature_text` VALUES ('31283', '0', '0', 'Ah, the hand of the Ebon Blade has finally come to claim me. I tremble in fear.', '14', '0', '100', '0', '0', '0', 'Orbaz Bloodbane');
INSERT INTO `creature_text` VALUES ('31283', '1', '0', 'Hah! That\'s all you\'ve got? I\'m surprised you even made it to the citadel.', '14', '0', '100', '0', '0', '0', 'Orbaz Bloodbane');
INSERT INTO `creature_text` VALUES ('31283', '2', '0', 'The Lich King has given me power beyond your wildest nightmares. Behold!', '14', '0', '100', '0', '0', '0', 'Orbaz Bloodbane');
INSERT INTO `creature_text` VALUES ('31283', '3', '0', 'It all ends here.', '14', '0', '100', '0', '0', '0', 'Orbaz Bloodbane');
INSERT INTO `creature_text` VALUES ('31283', '4', '0', '%s chuckles knowingly.', '16', '0', '100', '0', '0', '0', 'Orbaz Bloodbane');
INSERT INTO `creature_text` VALUES ('31283', '5', '0', 'The Lich King shall extinquish... every... last... one... of you.', '14', '0', '100', '0', '0', '0', 'Orbaz Bloodbane');
INSERT INTO `creature_text` VALUES ('31283', '6', '0', 'There you are Darion! I was wondering when you\'d crawl forth!', '14', '0', '100', '0', '0', '0', 'Orbaz Bloodbane');
INSERT INTO `creature_text` VALUES ('31290', '0', '0', 'Orbaz Bloodbane, self proclaimed hand of the Lich King.', '14', '0', '100', '0', '0', '0', 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES ('31290', '1', '0', 'Couldn\'t wait for me to be out of the way so that you could sieze control?', '14', '0', '100', '0', '0', '0', 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES ('31290', '2', '0', 'You\'ll never have what it takes to lead anything, Orbaz, not even a legion of mindless skeletons.', '14', '0', '100', '0', '0', '0', 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES ('31290', '3', '0', 'Good riddance.', '14', '0', '100', '0', '0', '0', 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES ('17843', '0', '0', 'I want all of your trackers out in search of the criminal, Matis the Cruel. I have given trusted agents of the Hand flare guns. Should you or one of your trackers see the flare, it means that an agent has found Matis and needs assistance. And remember, tracker, Matis is to be brought back to me alive.', '12', '7', '100', '1', '0', '0', 'Vindicator Kuros');
INSERT INTO `creature_text` VALUES ('17875', '0', '0', 'I am at your service, my lord.', '12', '7', '100', '0', '0', '0', 'Hunter of the Hand');
INSERT INTO `creature_text` VALUES ('17875', '1', '0', 'It will be done, my lord.', '12', '7', '100', '1', '0', '0', 'Hunter of the Hand');
INSERT INTO `creature_text` VALUES ('17875', '2', '0', '%s calls forth their bretheren.', '16', '7', '100', '22', '0', '0', 'Hunter of the Hand');
INSERT INTO `creature_text` VALUES ('17875', '3', '0', 'Brothers and sisters, tonight we hunt! A blood elf known as Matis must be brought to justice! The first to find him gets one week\'s vacation aboard the Exodar!', '12', '7', '100', '1', '0', '0', 'Hunter of the Hand');
INSERT INTO `creature_text` VALUES ('17875', '4', '0', 'Onward! The wilds of Bloodmyst await!', '12', '7', '100', '1', '0', '0', 'Hunter of the Hand');
INSERT INTO `creature_text` VALUES ('17853', '0', '0', 'We\'ve got you now, Matis the Cruel!', '12', '0', '100', '0', '0', '0', 'Tracker of the Hand');
INSERT INTO `creature_text` VALUES ('17853', '1', '0', 'Return to Kuros. I will bring him to Blood Watch.', '12', '7', '100', '0', '0', '0', 'Tracker of the Hand');
INSERT INTO `creature_text` VALUES ('17664', '0', '0', 'You will regret this, maggot.', '12', '0', '100', '0', '0', '0', 'Matis the Cruel');
INSERT INTO `creature_text` VALUES ('17664', '1', '0', '%s falls unconscious.', '16', '0', '100', '0', '0', '0', 'Matis the Cruel');
INSERT INTO `creature_text` VALUES ('29932', '0', '0', 'Something stirs in the pool of mojo...', '16', '0', '100', '0', '0', '0', 'Eck the Ferocious');
INSERT INTO `creature_text` VALUES ('29573', '0', '0', '%s gathers its mojo and surges forward!', '16', '0', '100', '0', '0', '0', 'Drakkari Elemental - Mojo');
INSERT INTO `creature_text` VALUES ('29573', '1', '0', 'As the last remnants of the %s seep into the ground, the altar set into the floor nearby begins to glow faintly.', '16', '0', '100', '0', '0', '0', 'Drakkari Elemental - Death');
INSERT INTO `creature_text` VALUES ('29394', '0', '0', 'Not yet, Ymiron, I have other plans for you. You will serve me better within Utgarde Pinnacle. And if these insects survive to find you again, you will get the chance to avenge your wife.', '14', '0', '100', '1', '0', '14748', 'The Lich King');
INSERT INTO `creature_text` VALUES ('24023', '0', '0', 'Give me your souls that I might slay the enemy!', '14', '0', '100', '0', '0', '13774', 'Queen Angerboda');
INSERT INTO `creature_text` VALUES ('24023', '1', '0', 'You will not stop my work here!  My husband-king will awaken!', '14', '0', '100', '0', '0', '13775', 'Queen Angerboda');
INSERT INTO `creature_text` VALUES ('24023', '2', '0', 'The ritual proceeds too slowly. More sacrifices are needed!', '14', '0', '100', '0', '0', '0', 'Queen Angerboda');
INSERT INTO `creature_text` VALUES ('24023', '2', '1', 'Soon, my love. Soon you will waken and all of Azeroth will know fear!', '14', '0', '100', '0', '0', '0', 'Queen Angerboda');
INSERT INTO `creature_text` VALUES ('24023', '2', '2', 'Awaken, Ymiron, the time of your continued reign is nigh.', '14', '0', '100', '0', '0', '0', 'Queen Angerboda');
INSERT INTO `creature_text` VALUES ('24023', '2', '3', 'Double your efforts! King Ymiron will awaken and then he will ascend!', '14', '0', '100', '0', '0', '0', 'Queen Angerboda');
INSERT INTO `creature_text` VALUES ('24023', '2', '4', 'Sleep no longer, my love. With the might of the Lich King behind you, the world will tremble.', '14', '0', '100', '0', '0', '0', 'Queen Angerboda');
INSERT INTO `creature_text` VALUES ('24023', '2', '5', 'Why is this ritual taking so long? Your master promised that your assistance would speed the process!', '14', '0', '100', '0', '0', '0', 'Queen Angerboda');
INSERT INTO `creature_text` VALUES ('23780', '1', '0', 'Send these scumbags back to hell!  Fire at will!', '14', '0', '100', '0', '0', '0', 'High Executor Anselm');
INSERT INTO `creature_text` VALUES ('23780', '2', '0', 'Archers at the ready!  Hold your fire!', '14', '0', '100', '0', '0', '0', 'High Executor Anselm');
INSERT INTO `creature_text` VALUES ('23780', '3', '0', 'What fool dares to enter her majesty\'s dominion unannounced?', '14', '0', '100', '0', '0', '0', 'High Executor Anselm');
INSERT INTO `creature_text` VALUES ('23780', '4', '0', 'Is that all you\'ve come to say?', '12', '0', '100', '0', '0', '0', 'High Executor Anselm');
INSERT INTO `creature_text` VALUES ('24041', '0', '0', 'Arthas is prepared to offer you power beyond your imagination.  The puny army you lead here would pale in comparison to the phalanxes at your command if you returned to the Scourge\'s embrace.', '12', '0', '100', '0', '0', '0', 'Prince Keleseth');
INSERT INTO `creature_text` VALUES ('24041', '1', '0', 'Behold the Vrykul!   A race that has perfected war and destruction to the point of an art form.  Already they\'ve cast their lot with the Lich King!  Their dwellings surround you and their numbers are easily five times yours.', '12', '0', '100', '0', '0', '0', 'Prince Keleseth');
INSERT INTO `creature_text` VALUES ('24041', '2', '0', 'The choice is yours, Anselm.  Return to the Lich King\'s army and fight alongside them or remain loyal to your so-called queen and suffer their wrath as they drive you from their homelands!', '12', '0', '100', '0', '0', '0', 'Prince Keleseth');
INSERT INTO `creature_text` VALUES ('24041', '3', '0', 'Such a futile gesture.', '12', '0', '100', '0', '0', '0', 'Prince Keleseth');
INSERT INTO `creature_text` VALUES ('24041', '4', '0', 'Listen to your men\'s dying breaths as I drink in their souls!', '12', '0', '100', '0', '0', '0', 'Prince Keleseth');
INSERT INTO `creature_text` VALUES ('24041', '5', '0', 'This will not be the last you hear of me.  I will return to spit on your corpse after Utgarde\'s armies have descended upon you!', '12', '0', '100', '25', '0', '0', 'Prince Keleseth');
INSERT INTO `creature_text` VALUES ('23778', '0', '0', 'Keep them split from their main force!', '14', '0', '100', '0', '0', '0', 'Dark Ranger Lyana');
INSERT INTO `creature_text` VALUES ('23778', '0', '1', 'Cover fire, now!', '14', '0', '100', '0', '0', '0', 'Dark Ranger Lyana');
INSERT INTO `creature_text` VALUES ('23778', '0', '2', 'Keep firing!  Cut off their reinforcements!', '14', '0', '100', '0', '0', '0', 'Dark Ranger Lyana');
INSERT INTO `creature_text` VALUES ('24252', '0', '0', 'The foot bone\'s connected to the... hip bone!  The hip bone\'s connected to this... other bone!  Hmmmhmmmmhmmm...', '12', '1', '100', '0', '0', '0', '\"Hacksaw\" Jenny');
INSERT INTO `creature_text` VALUES ('24252', '0', '1', 'It\'s hard work putting these things together!', '12', '1', '100', '0', '0', '0', '\"Hacksaw\" Jenny');
INSERT INTO `creature_text` VALUES ('28313', '0', '0', 'Longrunner, your fodder... err, people will be a valuable resource against the vrykul. Did I say resource? I meant, \'ally.\' ', '12', '1', '100', '25', '0', '0', 'Apothecary Scyllis');
INSERT INTO `creature_text` VALUES ('7564', '0', '0', 'I\'m going to carefully mix this - oops! ...Well, I spilled a little, but it\'s ready!', '12', '0', '100', '0', '0', '0', 'Marin Noggenfogger');
INSERT INTO `creature_text` VALUES ('7583', '0', '0', 'Just sit tight while i get this prepared for you!', '12', '0', '100', '0', '0', '0', 'Sprinkle');
INSERT INTO `creature_text` VALUES ('32184', '3', '0', 'I call your bluff.  You\'re a paladin after all.  Your obsession with redemption goes beyond the inane.', '12', '0', '100', '1', '0', '0', 'The Lich King');
INSERT INTO `creature_text` VALUES ('32184', '2', '0', 'Last time we met, you had the advantage of fighting on holy ground.  You\'ll find that our situation has been... reversed.', '12', '0', '100', '11', '0', '0', 'The Lich King');
INSERT INTO `creature_text` VALUES ('32184', '1', '0', 'I must confess... you were not altogether unexpected.  I hope you find your final resting place... to your liking.', '12', '0', '100', '1', '0', '0', 'The Lich King');
INSERT INTO `creature_text` VALUES ('32184', '0', '0', 'Uninvited guests!  Did you think you\'d go unnoticed inside my dominion?', '12', '0', '100', '1', '0', '0', 'The Lich King');
INSERT INTO `creature_text` VALUES ('32312', '0', '0', 'Quick, through the portal!  He won\'t stay down for long.', '12', '0', '100', '0', '0', '0', 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES ('32312', '1', '0', 'Patience... we will get our chance soon enough.  Be content that for once, it is Tirion who is in our debt.', '12', '0', '100', '0', '0', '0', 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES ('32311', '1', '0', 'The Lich King is badly hurt.  We ought to stay behind and finish him.', '12', '0', '100', '0', '0', '0', 'Koltira Deathweaver');
INSERT INTO `creature_text` VALUES ('32311', '0', '0', 'Take courage, crusaders.  You do not fight alone!', '14', '0', '100', '0', '0', '0', 'Koltira Deathweaver');
INSERT INTO `creature_text` VALUES ('32239', '8', '0', 'Only shadows from the past remain.  There\'s nothing left to redeem!', '12', '0', '100', '0', '0', '0', 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES ('32239', '0', '0', 'It is time.  May the Light give us strength.', '12', '0', '100', '0', '0', '0', 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES ('32239', '1', '0', 'Keep your heads down and follow my lead.', '12', '0', '100', '0', '0', '0', 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES ('32239', '2', '0', 'Here it comes.  Stand ready.', '12', '0', '100', '0', '0', '0', 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES ('32239', '3', '0', 'Something\'s wrong... I sense a dark presence.', '12', '0', '100', '0', '0', '0', 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES ('32239', '4', '0', 'The Lich King is here.  May the Light guide our blades.', '12', '0', '100', '0', '0', '0', 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES ('32239', '5', '0', 'You sound a little too confident.  Especially considering the way our last encounter ended.', '12', '0', '100', '25', '0', '0', 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES ('32239', '6', '0', 'That might be, but I don\'t need to stand on holy ground to run that disembodied heart of yours with the Ashbringer.', '12', '0', '100', '1', '0', '0', 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES ('32239', '7', '0', 'The heart... the last remaining vestige of your humanity.  I had to stop it from being destroyed.  I had to see for myself.  And at last I\'m sure...', '12', '0', '100', '1', '0', '0', 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES ('30215', '2', '0', 'The Helm of Hodir!', '42', '0', '100', '0', '0', '0', 'Player - Read Pronouncement');
INSERT INTO `creature_text` VALUES ('30215', '1', '0', 'Sons of Hodir! I humbly present to you....', '42', '0', '100', '0', '0', '0', 'Player - Read Pronouncement');
INSERT INTO `creature_text` VALUES ('21024', '7', '0', 'We will find these words. We will summon your master and we will destroy him.', '12', '0', '100', '1', '0', '0', 'Earthmender Torlok');
INSERT INTO `creature_text` VALUES ('21130', '2', '0', 'Yes... Gul\'dan...', '12', '0', '100', '0', '0', '0', 'Scorned Spirit of Fire');
INSERT INTO `creature_text` VALUES ('21132', '1', '0', 'You must utter the words...', '12', '0', '100', '0', '0', '0', 'Scorned Spirit of Air');
INSERT INTO `creature_text` VALUES ('21129', '2', '0', 'Yes... Yes... the words. The words of damnation. The words spoken by the dark one. Your master.', '12', '0', '100', '0', '0', '0', 'Scorned Spirit of Earth');
INSERT INTO `creature_text` VALUES ('21024', '3', '0', 'Please tell us this: Who or what is Cyrukh?', '12', '0', '100', '1', '0', '0', 'Earthmender Torlok');
INSERT INTO `creature_text` VALUES ('21129', '1', '0', 'Fool! Cyrukh the fire lord! When the tie was severed, Cyrukh awoke. It is the tears of Cyrukh that fill this land with molten rock.', '12', '0', '100', '0', '0', '0', 'Scorned Spirit of Earth');
INSERT INTO `creature_text` VALUES ('21024', '4', '0', 'The... The fire lord? An elemental lord was unearthed? Of course... Gul\'dan you were a damned fool!', '12', '0', '100', '6', '0', '0', 'Earthmender Torlok');
INSERT INTO `creature_text` VALUES ('21130', '1', '0', 'He is insane now. Driven mad during the shattering. There is no rest for the elements.', '12', '0', '100', '0', '0', '0', 'Scorned Spirit of Fire');
INSERT INTO `creature_text` VALUES ('21024', '5', '0', 'There must be a way.', '12', '0', '100', '0', '0', '0', 'Earthmender Torlok');
INSERT INTO `creature_text` VALUES ('21131', '1', '0', 'Yes, a way. Destroy Cyrukh. Destroy an elemental lord.', '12', '0', '100', '0', '0', '0', 'Scorned Spirit of Water');
INSERT INTO `creature_text` VALUES ('21024', '6', '0', 'Do not doubt my comrades and I, spirit. Tell us how to conjure the fire lord. My allies will destroy him.', '12', '0', '100', '1', '0', '0', 'Earthmender Torlok');
INSERT INTO `creature_text` VALUES ('21024', '0', '0', 'Stand back, $n. The spirits may lash out.', '12', '0', '100', '1', '0', '0', 'Earthmender Torlok');
INSERT INTO `creature_text` VALUES ('21129', '0', '0', 'Why have you captured us?', '12', '0', '100', '0', '0', '0', 'Scorned Spirit of Earth');
INSERT INTO `creature_text` VALUES ('21130', '0', '0', 'What is it that you hope to gain from this?', '12', '0', '100', '0', '0', '0', 'Scorned Spirit of Fire');
INSERT INTO `creature_text` VALUES ('21024', '1', '0', 'We wish to help you, spirit. We seek your wisdom.', '12', '0', '100', '1', '0', '0', 'Earthmender Torlok');
INSERT INTO `creature_text` VALUES ('21131', '0', '0', 'Wisdom? It was your people that caused the insufferable wrath of Cyrukh.', '12', '0', '100', '0', '0', '0', 'Scorned Spirit of Water');
INSERT INTO `creature_text` VALUES ('21132', '0', '0', 'Why should we help you? The world is ruined. Destroyed.', '12', '0', '100', '0', '0', '0', 'Scorned Spirit of Air');
INSERT INTO `creature_text` VALUES ('21024', '2', '0', 'You are right, spirit. It was my people that did this to you. Allow us to make it right.', '12', '0', '100', '1', '0', '0', 'Earthmender Torlok');
INSERT INTO `creature_text` VALUES ('23364', '0', '0', 'The %s screams out for its mother.', '16', '0', '100', '0', '0', '431', 'Black Dragon Whelpling');
INSERT INTO `creature_text` VALUES ('23282', '0', '0', 'I believe that I shall feast upon both $r and ogre flesh tonight.', '14', '0', '100', '0', '0', '7274', 'Obsidia');
INSERT INTO `creature_text` VALUES ('20482', '0', '0', 'Hostiles detected. Ending transmission.', '12', '0', '100', '15', '0', '0', 'Image of Commander Ameer');
INSERT INTO `creature_text` VALUES ('19544', '1', '0', 'There it is! I could\'ve sworn it wasn\'t here last time I checked...', '12', '0', '100', '0', '0', '0', 'Conjurer Luminrath');
INSERT INTO `creature_text` VALUES ('19545', '0', '0', 'Phew! There\'s my lucky hat. I\'ve been looking for it everywhere.', '12', '0', '100', '0', '0', '0', 'Cohlien Frostweaver');
INSERT INTO `creature_text` VALUES ('25834', '0', '0', 'You were looking for me, child? Why do you come to kill me, $n? I only wish to help.', '12', '0', '100', '6', '0', '0', 'Gearmaster Mechazod');
INSERT INTO `creature_text` VALUES ('25380', '0', '0', 'And remember, $n, the bloodspore powder can be used to weaken all magnataur in Gammoth.', '15', '0', '100', '0', '0', '0', 'Primal Mighthorn');
INSERT INTO `creature_text` VALUES ('25602', '0', '0', 'Now, $n, let us release their souls and hear what they have to say.', '12', '0', '100', '1', '0', '0', 'Greatmother Taiga');
INSERT INTO `creature_text` VALUES ('25602', '1', '0', 'This is dire news indeed!', '12', '0', '100', '1', '0', '0', 'Greatmother Taiga');
INSERT INTO `creature_text` VALUES ('26097', '1', '0', 'In return, he \'de-cursed\' us, saying that we were being \'fixed\' by returning us to the state of being mechanical constructs.', '12', '0', '100', '0', '0', '0', 'Fizzcrank Gnome Ghost');
INSERT INTO `creature_text` VALUES ('26097', '2', '0', 'He claims that all creations of the Titans fall prey to what he calls, \'The Curse of Flesh\'. He says that the original gnomes were once mechagnomes.', '12', '0', '100', '0', '0', '0', 'Fizzcrank Gnome Ghost');
INSERT INTO `creature_text` VALUES ('26097', '3', '0', 'He\'s quite insane! Someone must put a stop to his madness before it\'s too late for everyone!', '12', '0', '100', '0', '0', '0', 'Fizzcrank Gnome Ghost');
INSERT INTO `creature_text` VALUES ('25849', '1', '0', 'It works, Dorain! Look! It actually works...', '12', '0', '100', '4', '0', '0', 'Fezzix Geartwist');
INSERT INTO `creature_text` VALUES ('25849', '2', '0', 'Here I goooooooooo!', '12', '0', '100', '0', '0', '0', 'Fezzix Geartwist');
INSERT INTO `creature_text` VALUES ('25849', '0', '0', 'It\'s really going to work this time... right?', '12', '0', '100', '6', '0', '0', 'Fezzix Geartwist');
INSERT INTO `creature_text` VALUES ('26097', '0', '0', 'Thank you for saving us. We accidentally uncovered the pieces of Gearmaster Mechazod and reassembled him.', '12', '0', '100', '0', '0', '0', 'Fizzcrank Gnome Ghost');
INSERT INTO `creature_text` VALUES ('25834', '3', '0', 'Very well. It saddens me that it has come to this. I look upon all of you as if you were my children. I will slay you if I must!', '12', '0', '100', '25', '0', '0', 'Gearmaster Mechazod');
INSERT INTO `creature_text` VALUES ('25834', '2', '0', 'But, I can see it in your eyes, hear it in your pulse rate. You would destroy me despite my offer of immortality!', '12', '0', '100', '25', '0', '0', 'Gearmaster Mechazod');
INSERT INTO `creature_text` VALUES ('25834', '1', '0', 'Now that I have been reassembled, we can return to a time of perfection... the time of the Titans!', '12', '0', '100', '1', '0', '0', 'Gearmaster Mechazod');
INSERT INTO `creature_text` VALUES ('25983', '0', '0', 'Fezzix, I think we\'ve solved your little engine problem.', '12', '0', '100', '1', '0', '0', 'Dorain Frosthoof');
INSERT INTO `creature_text` VALUES ('25849', '4', '0', 'I give up! So much for goblin ingenuity!', '12', '0', '100', '5', '0', '0', 'Fezzix Geartwist');
INSERT INTO `creature_text` VALUES ('25849', '3', '0', 'OUCH!', '12', '0', '100', '0', '0', '0', 'Fezzix Geartwist');
INSERT INTO `creature_text` VALUES ('32692', '0', '3', 'You couldn\'t ask for a finer person than $n, I say.', '12', '0', '100', '0', '0', '0', 'Arcanist Alec');
INSERT INTO `creature_text` VALUES ('32692', '0', '2', 'A fine example of a $g man:woman;, that $n.', '12', '0', '100', '0', '0', '0', 'Arcanist Alec');
INSERT INTO `creature_text` VALUES ('32692', '0', '1', '$n, you\'re an inspiration.', '12', '0', '100', '0', '0', '0', 'Arcanist Alec');
INSERT INTO `creature_text` VALUES ('32692', '0', '0', 'There goes the finest $c I have the honor of knowing.', '12', '0', '100', '0', '0', '0', 'Arcanist Alec');
INSERT INTO `creature_text` VALUES ('32687', '0', '5', '$n! Ah, fantastic to see you out and about. Busy as always, hm?', '12', '0', '100', '0', '0', '0', 'Linda Ann Kastinglow');
INSERT INTO `creature_text` VALUES ('32687', '0', '4', '$n, wonderful to see you. Wonderful.', '12', '0', '100', '0', '0', '0', 'Linda Ann Kastinglow');
INSERT INTO `creature_text` VALUES ('32687', '0', '3', 'You couldn\'t ask for a finer person than $n, I say.', '12', '0', '100', '0', '0', '0', 'Linda Ann Kastinglow');
INSERT INTO `creature_text` VALUES ('32687', '0', '2', 'A fine example of a $g man:woman;, that $n.', '12', '0', '100', '0', '0', '0', 'Linda Ann Kastinglow');
INSERT INTO `creature_text` VALUES ('32687', '0', '1', '$n, you\'re an inspiration.', '12', '0', '100', '0', '0', '0', 'Linda Ann Kastinglow');
INSERT INTO `creature_text` VALUES ('32687', '0', '0', 'There goes the finest $c I have the honor of knowing.', '12', '0', '100', '0', '0', '0', 'Linda Ann Kastinglow');
INSERT INTO `creature_text` VALUES ('25301', '0', '0', 'Our troops, general, consist mostly of villagers and peasants.  Good men, but not quite rid of the prejudices and superstitions of their upbringing.  They\'re not ready to fight alongside our more exotic allies.', '12', '7', '100', '1', '0', '0', 'Counselor Talbot');
INSERT INTO `creature_text` VALUES ('25285', '0', '0', 'Give the word, general.  I will have two regiments at your disposal in a month\'s time.', '12', '7', '100', '0', '0', '0', 'Harbinger Vurenn');
INSERT INTO `creature_text` VALUES ('25285', '1', '0', 'Very well, general.  Should you change your mind, my people will be more than willing to provide military assistance.', '12', '7', '100', '0', '0', '0', 'Harbinger Vurenn');
INSERT INTO `creature_text` VALUES ('31033', '0', '0', 'There goes the hero of the Vanguard!', '12', '0', '100', '66', '0', '0', 'Crusader of Virtue');
INSERT INTO `creature_text` VALUES ('31033', '0', '1', '%s cheers at you.', '16', '1', '100', '71', '0', '0', 'Crusader of Virtue');
INSERT INTO `creature_text` VALUES ('31033', '0', '2', 'We couldn\'t have done it without you, $g sir:ma\'am;.', '12', '0', '100', '66', '0', '0', 'Crusader of Virtue');
INSERT INTO `creature_text` VALUES ('31033', '0', '3', 'You honor us with your presence, $n.', '12', '0', '100', '66', '0', '0', 'Crusader of Virtue');
INSERT INTO `creature_text` VALUES ('31033', '0', '4', 'Thank you, $n. From the bottom of my heart. Thank you.', '12', '0', '100', '66', '0', '0', 'Crusader of Virtue');
INSERT INTO `creature_text` VALUES ('23734', '4', '0', 'Don\'t worry, soldier. Yazmina is going to take care of you.', '12', '7', '100', '1', '0', '0', 'Anchorite Yazmina');
INSERT INTO `creature_text` VALUES ('23734', '3', '0', 'Oh my, let me change those bandages!', '12', '7', '100', '5', '0', '0', 'Anchorite Yazmina');
INSERT INTO `creature_text` VALUES ('23734', '2', '0', 'I just don\'t have the medical supplies I need to properly treat all of you.', '12', '7', '100', '1', '0', '5495', 'Anchorite Yazmina');
INSERT INTO `creature_text` VALUES ('23734', '1', '0', 'I can only hope the treatment will work.', '12', '7', '100', '1', '0', '0', 'Anchorite Yazmina');
INSERT INTO `creature_text` VALUES ('23734', '0', '0', 'That should slow the infection down a little.', '12', '7', '100', '1', '0', '0', 'Anchorite Yazmina');
INSERT INTO `creature_text` VALUES ('23728', '2', '0', 'So when you\'re feeling that tinglin\' sensation at the base of your spine and you\'re \'bout ready to wet your pants, embrace it. Let it enrage you. That fear is the only thing that\'s gonna keep you breathin\'.', '12', '7', '100', '25', '0', '0', 'Guard Captain Zorek');
INSERT INTO `creature_text` VALUES ('23728', '1', '0', 'I look across this base and all I see is fear... Are you gonna be scared when the Lich King\'s armies are sittin\' pretty in Stormwind? Ironforge? NO! You\'re not. And do you know why? Because you\'ll be dead, that\'s why.', '12', '7', '100', '1', '0', '0', 'Guard Captain Zorek');
INSERT INTO `creature_text` VALUES ('23734', '5', '0', '%s shakes her head.', '16', '7', '100', '274', '0', '0', 'Anchorite Yazmina');
INSERT INTO `creature_text` VALUES ('23728', '0', '0', '%s clears his throat.', '16', '7', '100', '0', '0', '0', 'Guard Captain Zorek');
INSERT INTO `creature_text` VALUES ('24852', '1', '0', 'System failure in 3... 2...', '12', '0', '100', '0', '0', '0', 'Iron Rune Construct');
INSERT INTO `creature_text` VALUES ('24852', '0', '0', 'Launching.', '12', '0', '100', '0', '0', '0', 'Iron Rune Construct');
INSERT INTO `creature_text` VALUES ('31238', '1', '1', '%s looks around Krasus\' Landing, bored.', '16', '0', '100', '0', '0', '0', 'Hira Snowdawn');
INSERT INTO `creature_text` VALUES ('31238', '1', '0', '%s sighs softly and rolls her shoulders.', '16', '0', '100', '0', '0', '0', 'Hira Snowdawn');
INSERT INTO `creature_text` VALUES ('31238', '0', '2', 'I\'m bored, Cloudwing. Go, go!', '12', '0', '100', '0', '0', '0', 'Hira Snowdawn');
INSERT INTO `creature_text` VALUES ('31238', '0', '1', 'Feeling as restless as I am, Cloudwing? Then let us fly!', '12', '0', '100', '0', '0', '0', 'Hira Snowdawn');
INSERT INTO `creature_text` VALUES ('31238', '0', '0', 'Come, Cloudwing. Let\'s do a lap.', '12', '0', '100', '0', '0', '0', 'Hira Snowdawn');
INSERT INTO `creature_text` VALUES ('19217', '0', '0', 'Oh, another show already? Very well then...', '12', '0', '100', '1', '0', '0', 'Ravandwyr');
INSERT INTO `creature_text` VALUES ('19610', '8', '0', 'Don\'t worry. It\'s an improvement!', '12', '0', '100', '11', '0', '0', 'Irradiated Worker');
INSERT INTO `creature_text` VALUES ('19610', '7', '0', 'My eyebrows! You singed my eyebrows!', '12', '0', '100', '22', '0', '0', 'Irradiated Worker');
INSERT INTO `creature_text` VALUES ('19610', '6', '0', 'That was the best trick ever! Ok, boys, let\'s go get a drink.', '12', '0', '100', '4', '0', '0', 'Irradiated Worker');
INSERT INTO `creature_text` VALUES ('19610', '5', '0', 'Remember how you\'re always telling us to chill?', '12', '0', '100', '11', '0', '0', 'Irradiated Worker');
INSERT INTO `creature_text` VALUES ('19610', '0', '0', 'A skunk! Eeeeeeeeeeeeeeeeeew! Don\'t touch me!', '12', '0', '100', '0', '0', '0', 'Irradiated Worker');
INSERT INTO `creature_text` VALUES ('19610', '1', '0', 'Get it away from me!', '12', '0', '100', '0', '0', '0', 'Irradiated Worker');
INSERT INTO `creature_text` VALUES ('19610', '2', '0', 'Show us a trick, Ravandwyr!', '12', '0', '100', '0', '0', '0', 'Irradiated Worker');
INSERT INTO `creature_text` VALUES ('19610', '3', '0', 'Let me outta here! Guys... guys?', '12', '0', '100', '0', '0', '0', 'Irradiated Worker');
INSERT INTO `creature_text` VALUES ('19610', '4', '0', 'Oh man, he got you good!', '12', '0', '100', '0', '0', '0', 'Irradiated Worker');
INSERT INTO `creature_text` VALUES ('19217', '2', '0', 'I shall transform my familiar, Sagan, into something truly wondrous!', '12', '0', '100', '25', '0', '0', 'Ravandwyr');
INSERT INTO `creature_text` VALUES ('19217', '1', '0', '%s sighs audibly and glances at Sagan.', '16', '0', '100', '0', '0', '0', 'Ravandwyr');
INSERT INTO `creature_text` VALUES ('35003', '0', '0', 'All aboard the gunship! Man the guns!', '14', '0', '100', '0', '0', '0', 'Horde Gunship Captain');
INSERT INTO `creature_text` VALUES ('34960', '0', '0', 'All aboard the gunship! Man the guns!', '14', '0', '100', '0', '0', '0', 'Alliance Gunship Captain');
INSERT INTO `creature_text` VALUES ('49476', '0', '0', 'RAZ, NOOOOOOOOOOO!!!!', '14', '0', '100', '0', '0', '0', 'Finkle Einhorn');
INSERT INTO `creature_text` VALUES ('32184', '4', '0', 'You surely wouldn\'t destroy humanity\'s only chance to redeem its most wayward son.  You\'d sooner die!', '12', '0', '100', '1', '0', '0', 'The Lich King');
INSERT INTO `creature_text` VALUES ('32184', '5', '0', 'Arrrrggggggggggh!!!!', '14', '0', '100', '34', '0', '0', 'The Lich King');
INSERT INTO `creature_text` VALUES ('32184', '6', '0', 'You... will pay for that, old man.  Slay them all!', '14', '0', '100', '0', '0', '0', 'The Lich King');
INSERT INTO `creature_text` VALUES ('32241', '0', '0', 'Tirion\'s down!  Defend him with your lives!', '14', '0', '100', '0', '0', '0', 'Disguised Crusader');
